import React, { useState, useRef, useEffect } from "react";

const ChatUI = () => {
    const [messages, setMessages] = useState([
        { sender: "bot", text: "Bun venit în chat! Cu ce te pot ajuta?" }
    ]);
    const [input, setInput] = useState("");
    const messagesEndRef = useRef(null);

    const sendMessage = () => {
        if (!input.trim()) return;

        setMessages([...messages, { sender: "user", text: input }]);
        setInput("");
    };

    // Scroll automat la ultimul mesaj
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    return (
        <div
            style={{
                display: "flex",
                flexDirection: "column",
                height: "100vh",
                width: "100%",
                background: "#f7f7f7",
            }}
        >
            {/* Zona de mesaje */}
            <div
                style={{
                    flex: 1,
                    padding: "20px",
                    overflowY: "auto",
                    display: "flex",
                    flexDirection: "column"
                }}
            >
                {messages.map((msg, index) => (
                    <div
                        key={index}
                        style={{
                            marginBottom: "15px",
                            display: "flex",
                            justifyContent:
                                msg.sender === "user" ? "flex-end" : "flex-start"
                        }}
                    >
                        <div
                            style={{
                                padding: "10px 15px",
                                borderRadius: "12px",
                                maxWidth: "70%",
                                background:
                                    msg.sender === "user" ? "#007bff" : "#e6e6e6",
                                color: msg.sender === "user" ? "white" : "black",
                            }}
                        >
                            {msg.text}
                        </div>
                    </div>
                ))}
                <div ref={messagesEndRef} />
            </div>

            {/* Input chat */}
            <div
                style={{
                    display: "flex",
                    padding: "15px",
                    background: "#fff",
                    borderTop: "1px solid #ddd"
                }}
            >
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Scrie un mesaj..."
                    style={{
                        flex: 1,
                        padding: "12px",
                        borderRadius: "8px",
                        border: "1px solid #ccc",
                        fontSize: "16px",
                        marginRight: "10px"
                    }}
                    onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                />

                <button
                    onClick={sendMessage}
                    style={{
                        padding: "12px 20px",
                        background: "#007bff",
                        color: "white",
                        border: "none",
                        borderRadius: "8px",
                        cursor: "pointer",
                        fontSize: "16px"
                    }}
                >
                    Trimite
                </button>
            </div>
        </div>
    );
};

export default ChatUI;
