// src/hooks/useDocumentData.js

import { useState, useEffect } from 'react';
import { collection, getDocs } from 'firebase/firestore';
import { db } from '../InitApp/firebase'; // Asigurați-vă că este calea corectă!

export const useDocumentData = () => {
    const [documents, setDocuments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDocuments = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "documentModels"));
                const documentsData = querySnapshot.docs.map(doc => ({
                    id: doc.id,
                    ...doc.data()
                }));
                setDocuments(documentsData);
                setLoading(false);
            } catch (err) {
                console.error("Eroare la preluarea documentelor:", err);
                setError("Nu am putut încărca modelele de documente din Firebase.");
                setLoading(false);
            }
        };

        fetchDocuments();
    }, []);

    return { documents, loading, error };
};
