import React from 'react';

const ChatHistorySidebar = ({ isOpen, onClose, history, loading }) => {
    if (!isOpen) return null;

    return (
        <div style={styles.sidebar}>
            <div style={styles.header}>
                <h3>Istoric Chat</h3>
                <button onClick={onClose} style={styles.closeBtn}>X</button>
            </div>
            <div style={styles.content}>
                {loading ? (
                    <p>Se Ã®ncarcÄƒ...</p>
                ) : history.length === 0 ? (
                    <p style={{ textAlign: 'center', color: '#666', marginTop: 20 }}>
                        Nu existÄƒ istoric.
                    </p>
                ) : (
                    history.map((msg, i) => (
                        <div key={i} style={{
                            ...styles.msgRow,
                            justifyContent: msg.sender === 'user' ? 'flex-end' : 'flex-start'
                        }}>
                            <div style={{
                                ...styles.bubble,
                                background: msg.sender === 'user' ? '#6c63ff' : '#f2f2f2',
                                color: msg.sender === 'user' ? '#fff' : '#000'
                            }}>
                                {msg.text}
                                {(msg.fileUrl) && (
                                    <div style={{ marginTop: 5, fontSize: '0.9em' }}>
                                        <a 
                                            href={msg.fileUrl} 
                                            target="_blank" 
                                            rel="noopener noreferrer" 
                                            style={{ color: 'inherit', textDecoration: 'underline' }}
                                        >
                                            ðŸ“„ Deschide document
                                        </a>
                                    </div>
                                )}
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

const styles = {
    sidebar: {
        width: '300px',
        height: '100%',
        backgroundColor: '#fff',
        borderLeft: '1px solid #eee',
        display: 'flex',
        flexDirection: 'column',
        boxShadow: '-2px 0 10px rgba(0,0,0,0.1)',
        position: 'absolute',
        right: 0,
        top: 0,
        zIndex: 100
    },
    header: {
        padding: '15px 20px',
        borderBottom: '1px solid #eee',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundColor: '#f9f9f9'
    },
    closeBtn: {
        background: 'none',
        border: 'none',
        fontSize: 20,
        cursor: 'pointer',
        color: '#666'
    },
    content: {
        flex: 1,
        overflowY: 'auto',
        padding: 15,
        display: 'flex',
        flexDirection: 'column',
        gap: 10
    },
    msgRow: {
        display: 'flex',
        width: '100%'
    },
    bubble: {
        maxWidth: '90%',
        padding: '8px 12px',
        borderRadius: 12,
        whiteSpace: 'pre-wrap',
        fontSize: 13,
        lineHeight: '1.4'
    }
};

export default ChatHistorySidebar;
