import React, { useState, useRef, useEffect } from "react";
import { messageService } from "./messageService";

// Document definitions
const DOCUMENT_TYPES = {
    cerere: {
        fields: [
            "TipCerere", "PartiImplicate", "Context", "Conflict",
            "Argumente", "Termen", "Ton"
        ],
        questions: [
            "Ce tip de cerere dorești să generezi?",
            "Cine sunt părțile implicate?",
            "Care este contextul cererii?",
            "Există un conflict juridic?",
            "Argumente sau probe?",
            "Care este termenul dorit?",
            "Ce ton dorești să aibă cererea?"
        ]
    },

    contract: {
        fields: [
            "TipContract", "Parti", "Obiect", "Durata",
            "Pret", "Obligatii", "ClauzeSpeciale"
        ],
        questions: [
            "Ce tip de contract dorești să generezi?",
            "Cine sunt părțile contractului?",
            "Care este obiectul contractului?",
            "Care este durata contractului?",
            "Care este prețul sau contraprestația?",
            "Ce obligații trebuie specificate?",
            "Există clauze speciale?"
        ]
    },

    procura: {
        fields: [
            "TipProcura", "Mandant", "Mandatar", "Puteri",
            "Durata", "LocatiaSemnarii"
        ],
        questions: [
            "Ce tip de procură dorești să generezi?",
            "Cine este mandantul?",
            "Cine este mandatarul?",
            "Ce drepturi și puteri se acordă?",
            "Care este durata procurii?",
            "Unde va fi semnată procura?"
        ]
    }
};

const ChatUI = () => {
    const [messages, setMessages] = useState([
        { sender: "bot", text: "Salut! Ce tip de document dorești să generezi? (cerere / contract / procura)" }
    ]);

    const [input, setInput] = useState("");
    const [selectedDoc, setSelectedDoc] = useState(null);
    const [currentStep, setCurrentStep] = useState(0);
    const [formData, setFormData] = useState({});
    const messagesEndRef = useRef(null);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const sendMessage = async () => {
        if (!input.trim()) return;

        const userMsg = { sender: "user", text: input };
        setMessages(prev => [...prev, userMsg]);

        // Step 1 — choose document type
        if (!selectedDoc) {
            const type = input.toLowerCase();
            if (!DOCUMENT_TYPES[type]) {
                setMessages(prev => [
                    ...prev,
                    { sender: "bot", text: "Te rog alege: cerere, contract sau procura." }
                ]);
                setInput("");
                return;
            }

            setSelectedDoc(type);
            setMessages(prev => [
                ...prev,
                { sender: "bot", text: DOCUMENT_TYPES[type].questions[0] }
            ]);
            setInput("");
            return;
        }

        // Step 2 — fill required document data
        const doc = DOCUMENT_TYPES[selectedDoc];
        const field = doc.fields[currentStep];

        setFormData(prev => ({
            ...prev,
            [field]: input
        }));

        if (currentStep < doc.questions.length - 1) {
            setMessages(prev => [
                ...prev,
                { sender: "bot", text: doc.questions[currentStep + 1] }
            ]);
            setCurrentStep(prev => prev + 1);
        } else {
            // Step 3 — send to backend
            const payload = {
                ...formData,
                [field]: input,
                DocumentType: selectedDoc
            };

            const response = await messageService.sendMessage(payload);
            setMessages(prev => [...prev, response]);

            // Reset conversation
            setSelectedDoc(null);
            setCurrentStep(0);
            setFormData({});

            setMessages(prev => [
                ...prev,
                { sender: "bot", text: "Dacă dorești alt document, spune-mi ce tip: cerere / contract / procura." }
            ]);
        }

        setInput("");
    };

    return (
        <div
            style={{
                height: "calc(100vh - 140px)",
                display: "flex",
                flexDirection: "column",
                background: "#f7f7f7",
                borderRadius: "10px",
                boxShadow: "0 0 10px rgba(0,0,0,0.1)",
                overflow: "hidden"
            }}
        >
            {/* Chat messages */}
            <div
                style={{
                    flex: 1,
                    overflowY: "auto",
                    padding: "20px"
                }}
            >
                {messages.map((msg, idx) => (
                    <div
                        key={idx}
                        style={{
                            display: "flex",
                            justifyContent: msg.sender === "user" ? "flex-end" : "flex-start",
                            marginBottom: "10px"
                        }}
                    >
                        <div
                            style={{
                                padding: "12px 16px",
                                borderRadius: "12px",
                                maxWidth: "70%",
                                background: msg.sender === "user" ? "#6c63ff" : "#e6e6e6",
                                color: msg.sender === "user" ? "white" : "black"
                            }}
                        >
                            {msg.text}
                        </div>
                    </div>
                ))}
                <div ref={messagesEndRef}></div>
            </div>

            {/* Input bar */}
            <div
                style={{
                    display: "flex",
                    padding: "10px",
                    borderTop: "1px solid #ddd",
                    background: "white"
                }}
            >
                <input
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                    placeholder="Scrie un mesaj..."
                    style={{
                        flex: 1,
                        padding: "12px",
                        borderRadius: "8px",
                        border: "1px solid #ccc",
                        outline: "none"
                    }}
                />
                <button
                    onClick={sendMessage}
                    style={{
                        marginLeft: "10px",
                        padding: "12px 20px",
                        background: "#6c63ff",
                        color: "white",
                        border: "none",
                        borderRadius: "8px",
                        cursor: "pointer",
                        fontWeight: "bold"
                    }}
                >
                    Trimite
                </button>
            </div>
        </div>
    );
};

export default ChatUI;
