import React from 'react';

const ChatHistorySidebar = ({ isOpen, onClose, conversations, onSelectConversation, onNewConversation, loading }) => {
    if (!isOpen) return null;

    const formatDate = (timestamp) => {
        if (!timestamp) return '';
        const date = timestamp.toDate ? timestamp.toDate() : new Date(timestamp);
        return date.toLocaleDateString('ro-RO', { day: '2-digit', month: 'short' });
    };

    return (
        <div style={styles.sidebar}>
            <div style={styles.header}>
                <h3>Conversații</h3>
                <button onClick={onClose} style={styles.closeBtn}>X</button>
            </div>
            
            <div style={{ padding: '10px 15px' }}>
                <button onClick={onNewConversation} style={styles.newChatBtn}>
                    + Conversație Nouă
                </button>
            </div>

            <div style={styles.content}>
                {loading ? (
                    <p style={{ textAlign: 'center', color: '#666' }}>Se încarcă...</p>
                ) : conversations.length === 0 ? (
                    <p style={{ textAlign: 'center', color: '#666', marginTop: 20 }}>
                        Nu există conversații.
                    </p>
                ) : (
                    conversations.map((conv) => (
                        <div 
                            key={conv.id} 
                            onClick={() => onSelectConversation(conv.id)}
                            style={styles.convItem}
                        >
                            <div style={styles.convTitle}>{conv.title}</div>
                            <div style={styles.convMeta}>
                                <span style={styles.lastMsg}>{conv.lastMessage?.substring(0, 30)}...</span>
                                <span style={styles.date}>{formatDate(conv.updatedAt)}</span>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

const styles = {
    sidebar: {
        width: '300px',
        height: '100%',
        backgroundColor: '#fff',
        borderLeft: '1px solid #eee',
        display: 'flex',
        flexDirection: 'column',
        boxShadow: '-2px 0 10px rgba(0,0,0,0.1)',
        position: 'absolute',
        right: 0,
        top: 0,
        zIndex: 100
    },
    header: {
        padding: '15px 20px',
        borderBottom: '1px solid #eee',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundColor: '#f9f9f9'
    },
    closeBtn: {
        background: 'none',
        border: 'none',
        fontSize: 20,
        cursor: 'pointer',
        color: '#666'
    },
    newChatBtn: {
        width: '100%',
        padding: '10px',
        backgroundColor: '#4F46E5',
        color: 'white',
        border: 'none',
        borderRadius: '6px',
        cursor: 'pointer',
        fontWeight: '500'
    },
    content: {
        flex: 1,
        overflowY: 'auto',
        padding: '0 15px 15px 15px',
        display: 'flex',
        flexDirection: 'column',
        gap: 10
    },
    convItem: {
        padding: '12px',
        borderRadius: '8px',
        backgroundColor: '#f3f4f6',
        cursor: 'pointer',
        transition: 'background 0.2s',
        ':hover': {
            backgroundColor: '#e5e7eb'
        }
    },
    convTitle: {
        fontWeight: '600',
        fontSize: '14px',
        marginBottom: '4px',
        color: '#111827'
    },
    convMeta: {
        display: 'flex',
        justifyContent: 'space-between',
        fontSize: '12px',
        color: '#6B7280'
    },
    lastMsg: {
        overflow: 'hidden',
        textOverflow: 'ellipsis',
        whiteSpace: 'nowrap',
        maxWidth: '70%'
    }
};

export default ChatHistorySidebar;
