import React, { useState } from 'react';
import { registerUser } from '../AuthService';

const SignUp = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        try {
            // 1. Apelează serviciul care face atât Auth cât și Firestore save
            await registerUser(email, password, firstName, lastName, 'client');

            // Resetarea formularului și mesaj de succes
            setEmail('');
            setPassword('');
            setFirstName('');
            setLastName('');
            alert("Înregistrare finalizată cu succes!");
            // Aici poți adăuga logica de redirecționare, ex: navigate('/dashboard');

        } catch (err) {
            // Afișează eroarea returnată de Firebase (ex: parola prea scurtă)
            // Folosim .message pentru a afișa textul erorii.
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ maxWidth: '400px', margin: '0 auto', padding: '20px', border: '1px solid #ccc', borderRadius: '5px' }}>
            <h2>Înregistrare Cont Nou</h2>
            <form onSubmit={handleSubmit}>

                {error && <p style={{ color: 'red', fontWeight: 'bold' }}>Eroare: {error}</p>}

                {/* Nume și Prenume (pentru Firestore) */}
                <input
                    type="text"
                    placeholder="Prenume (First Name)"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Nume (Last Name)"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    required
                />

                {/* Câmpuri standard Auth */}
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Parolă (min. 6 caractere)"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />

                <button type="submit" disabled={loading} style={{ marginTop: '15px', padding: '10px' }}>
                    {loading ? 'Se procesează...' : 'Creare Cont'}
                </button>
            </form>
        </div>
    );
};

export default SignUp;
