import React, { useState } from 'react';
import { createUser } from '../AuthService';
import { useNavigate, Link } from "react-router-dom";

const SignUp = ({ onSignUp }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const navigate = useNavigate(); // pentru redirect

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);

        if (password !== confirmPassword) {
            return setError("Parolele nu coincid!");
        }

        setLoading(true);

        try {
            await createUser(email, password); // Firebase
            onSignUp(); // notificăm App.jsx

            navigate("/chat"); // redirect după sign up
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div
            style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                flexDirection: 'column',
                width: '100%',
            }}
        >
            <div
                style={{
                    backgroundColor: '#fff',
                    padding: '30px',
                    borderRadius: '10px',
                    maxWidth: '350px',
                    width: '100%',
                    boxShadow: '0px 4px 15px rgba(0,0,0,0.1)',
                }}
            >
                <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Creează Cont</h2>

                {error && (
                    <p style={{ color: 'red', textAlign: 'center' }}>
                        Eroare: {error}
                    </p>
                )}

                <form onSubmit={handleSubmit}>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Email"
                        required
                        style={inputStyle}
                    />

                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Parolă"
                        required
                        style={inputStyle}
                    />

                    <input
                        type="password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        placeholder="Confirmă Parola"
                        required
                        style={inputStyle}
                    />

                    <button
                        type="submit"
                        style={{
                            width: '100%',
                            backgroundColor: '#28a745',
                            border: 'none',
                            padding: '12px',
                            color: 'white',
                            fontSize: '16px',
                            borderRadius: '6px',
                            cursor: 'pointer',
                            marginTop: '10px',
                        }}
                        disabled={loading}
                    >
                        {loading ? 'Se creează...' : 'Creează Cont'}
                    </button>
                </form>

                <p
                    style={{
                        marginTop: '15px',
                        textAlign: 'center',
                        fontSize: '14px',
                    }}
                >
                    Ai deja cont?{' '}
                    <Link to="/login" style={{ color: '#007bff', cursor: 'pointer' }}>
                        Autentifică-te
                    </Link>
                </p>
            </div>
        </div>
    );
};

const inputStyle = {
    width: '100%',
    padding: '12px',
    marginBottom: '15px',
    borderRadius: '6px',
    border: '1px solid #ccc',
    fontSize: '16px',
    boxSizing: 'border-box',
};

export default SignUp;
