// src/App.jsx

import React from 'react';
import SignUp from './components/SignUp'; // Importă componenta de înregistrare
import Login from './components/Login';   // Importă componenta de autentificare

function App() {
  return (
      <div className="App" style={{ textAlign: 'center', padding: '20px' }}>
        <h1>Aplicația Mea Firebase/React</h1>

        {/* ---------------------------------------------------- */}
        {/* Secțiunea de Test: Formularul de Înregistrare */}
        {/* ---------------------------------------------------- */}
        <div style={{ margin: '40px auto', maxWidth: '400px', border: '1px solid #ddd', padding: '20px' }}>
          <h2>Înregistrare (Sign Up)</h2>
          <SignUp />
        </div>

        {/* ---------------------------------------------------- */}
        {/* Secțiunea de Test: Formularul de Autentificare */}
        {/* ---------------------------------------------------- */}
        <div style={{ margin: '40px auto', maxWidth: '400px', border: '1px solid #ddd', padding: '20px' }}>
          <h2>Autentificare (Login)</h2>
          <Login />
        </div>

      </div>
  );
}

export default App;
