const API_URL = "https://unilingual-tanisha-dissidently.ngrok-free.dev/api/generate";

export const messageService = {
    sendMessage: async (formData) => {
        try {
            const res = await fetch(API_URL, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(formData),
            });

            const data = await res.json();

            return {
                sender: "bot",
                text: data.response,
            };
        } catch (err) {
            console.error(err);
            return {
                sender: "bot",
                text: "Server error."
            };
        }
    }
};
