import React, { useEffect, useRef, useState } from "react";
import { ChatFlow } from "./ChatFlow";
import { DOCUMENT_TYPES } from "./models/documentTypes";
import { downloadDocx } from "./fileUtils";
import { useAuth } from "../../auth/AuthContext";
import { useChatHistory } from "../../hooks/useChatHistory";

// Create ONE flow instance (acts like a controller)
const chatFlow = new ChatFlow(DOCUMENT_TYPES);

const ChatUI = ({ messageService }) => {
    const { user } = useAuth();
    const { history, loading: historyLoading, saveMessage } = useChatHistory(user?.uid);

    const [localMessages, setLocalMessages] = useState([
        {
            sender: "bot",
            text: "üëã Salut! Ce tip de document dore»ôti sƒÉ generezi?\n(cerere / contract / procura)"
        }
    ]);

    const [input, setInput] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    const messagesEndRef = useRef(null);

    // Determine which messages to display
    // If logged in and history exists, show history. Otherwise show local (greeting).
    const messages = (user && history.length > 0) ? history : localMessages;

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages, isLoading]);

    const addMsg = (msg) => {
        if (user) {
            saveMessage(msg);
        } else {
            setLocalMessages(prev => [...prev, msg]);
        }
    };

    const sendMessage = async () => {
        if (!input.trim() || isLoading) return;

        const userInput = input.trim();
        setInput("");

        // show user message
        addMsg({ sender: "user", text: userInput });

        try {
            // STEP 1 ‚Äì select document
            if (!chatFlow.isDocumentSelected()) {
                const question = chatFlow.selectDocument(userInput.toLowerCase());

                addMsg({ sender: "bot", text: question });
                return;
            }

            // STEP 2 ‚Äì collect answers
            const result = chatFlow.handleAnswer(userInput);

            if (!result.done) {
                addMsg({ sender: "bot", text: result.nextQuestion });
                return;
            }

            // STEP 3 ‚Äì generate document
            setIsLoading(true);
            // We don't save the "Generez..." message to history usually, but we can if we want.
            // For now, let's just show it locally or save it. 
            // Saving "Generez..." might be annoying in history. 
            // Let's just set a local loading state which we already have (isLoading).
            // But we need to show the bubble.
            // We can add a temporary local message?
            // Or just rely on the `isLoading` indicator below.
            // The original code added a message.
            // Let's add it to localMessages only? No, that complicates the merge.
            // Let's just skip adding "Generez..." to the message list and rely on the "Bot scrie..." indicator 
            // or add a temporary message that we don't save?
            // For simplicity, let's just NOT add the "Generez..." text message, 
            // the "Bot scrie..." (isLoading) is enough feedback.
            
            const blob = await messageService.sendMessage(result.payload);

            addMsg({
                sender: "bot",
                text: "üìÑ Documentul este gata!",
                fileBlob: blob
            });
            
            addMsg({
                sender: "bot",
                text: "Vrei sƒÉ generezi un alt document?"
            });

            chatFlow.reset();
        } catch (err) {
            console.error(err);
            addMsg({ sender: "bot", text: "‚ùå A apƒÉrut o eroare. Te rog √ÆncearcƒÉ din nou." });
            chatFlow.reset();
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div style={styles.wrapper}>
            <div style={styles.chatContainer}>
                <div style={styles.messages}>
                    {messages.map((msg, i) => (
                        <div
                            key={i}
                            style={{
                                display: "flex",
                                justifyContent:
                                    msg.sender === "user" ? "flex-end" : "flex-start",
                                marginBottom: 10
                            }}
                        >
                            <div
                                style={{
                                    ...styles.bubble,
                                    background:
                                        msg.sender === "user" ? "#6c63ff" : "#f2f2f2",
                                    color: msg.sender === "user" ? "#fff" : "#000"
                                }}
                            >
                                {msg.text}

                                {(msg.fileBlob || msg.fileUrl) && (
                                    <div style={{ marginTop: 10 }}>
                                        <button
                                            style={styles.downloadBtn}
                                            onClick={() => {
                                                if (msg.fileBlob) {
                                                    downloadDocx(msg.fileBlob, "document.docx");
                                                } else if (msg.fileUrl) {
                                                    window.open(msg.fileUrl, "_blank");
                                                }
                                            }}
                                        >
                                            ‚¨áÔ∏è DescarcƒÉ documentul
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    ))}

                    {isLoading && (
                        <div style={styles.typing}>Bot scrie...</div>
                    )}

                    <div ref={messagesEndRef} />
                </div>

                {/* INPUT BAR */}
                <div style={styles.inputBar}>
                    <input
                        value={input}
                        onChange={e => setInput(e.target.value)}
                        onKeyDown={e => e.key === "Enter" && sendMessage()}
                        placeholder="Scrie rƒÉspunsul..."
                        style={styles.input}
                        disabled={isLoading}
                    />
                    <button
                        onClick={sendMessage}
                        style={styles.sendBtn}
                        disabled={isLoading}
                    >
                        Trimite
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ChatUI;

const styles = {
    wrapper: {
        height: "100%",
        display: "flex"
    },

    chatContainer: {
        width: "100%",
        height: "100%",
        display: "flex",
        flexDirection: "column",
        background: "#fff"
    },

    messages: {
        flex: 1,
        padding: 20,
        overflowY: "auto"
    },

    bubble: {
        maxWidth: "70%",
        padding: 14,
        borderRadius: 16,
        whiteSpace: "pre-wrap"
    },

    typing: {
        fontSize: 12,
        color: "#999",
        marginTop: 6
    },

    inputBar: {
        display: "flex",
        gap: 10,
        padding: 14,
        borderTop: "1px solid #eee",
        background: "#fff"
    },

    input: {
        flex: 1,
        padding: 12,
        borderRadius: 10,
        border: "1px solid #ddd",
        outline: "none"
    },

    sendBtn: {
        padding: "0 20px",
        borderRadius: 10,
        border: "none",
        background: "#6c63ff",
        color: "#fff",
        cursor: "pointer"
    },

    downloadBtn: {
        marginTop: 4,
        padding: "6px 10px",
        borderRadius: 6,
        border: "none",
        cursor: "pointer"
    }
};
