import React, { useState, useRef, useEffect } from "react";
import messageService from "./messageService";


// -----------------------------
// Document definitions
// -----------------------------
const DOCUMENT_TYPES = {
    cerere: {
        fields: [
            "TipCerere", "PartiImplicate", "Context", "Conflict",
            "Argumente", "Termen", "Ton"
        ],
        questions: [
            "Ce tip de cerere dore»ôti sƒÉ generezi?",
            "Cine sunt pƒÉr»õile implicate?",
            "Care este contextul cererii?",
            "ExistƒÉ un conflict juridic?",
            "Argumente sau probe?",
            "Care este termenul dorit?",
            "Ce ton dore»ôti sƒÉ aibƒÉ cererea?"
        ]
    },
    contract: {
        fields: [
            "TipContract", "PartiContractante", "Obiect", "Context",
            "Obligatii", "Durata", "Pret", "ClauzeSpeciale", "Ton"
        ],
        questions: [
            "Ce tip de contract dore»ôti sƒÉ generezi?",
            "Cine sunt pƒÉr»õile contractului?",
            "Care este obiectul contractului?",
            "Care este contextul contractului?",
            "Care sunt obliga»õiile?",
            "Care este durata?",
            "Care este pre»õul?",
            "ExistƒÉ clauze speciale?",
            "Ce ton dore»ôti?"
        ]
    },
    procura: {
        fields: [
            "TipProcura", "Mandant", "Mandatar", "Obiect",
            "Context", "Puterile", "Durata", "ClauzeSpeciale", "Ton"
        ],
        questions: [
            "Ce tip de procurƒÉ dore»ôti?",
            "Cine este mandantul?",
            "Cine este mandatarul?",
            "Care este obiectul?",
            "Care este contextul?",
            "Ce drepturi se acordƒÉ?",
            "Care este durata?",
            "Clauze speciale?",
            "Ce ton dore»ôti?"
        ]
    }
};

const ChatUI = () => {
    const [messages, setMessages] = useState([
        { sender: "bot", text: "Salut! Ce tip de document dore»ôti sƒÉ generezi? (cerere / contract / procura)" }
    ]);

    const [input, setInput] = useState("");
    const [selectedDoc, setSelectedDoc] = useState(null);
    const [currentStep, setCurrentStep] = useState(0);
    const [formData, setFormData] = useState({});
    const [isLoading, setIsLoading] = useState(false);

    const messagesEndRef = useRef(null);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages, isLoading]);

    const downloadDocx = (blob) => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = `${selectedDoc || "document"}.docx`;
        a.click();
        window.URL.revokeObjectURL(url);
    };

    const sendMessage = async () => {
        if (!input.trim() || isLoading) return;

        setMessages(prev => [...prev, { sender: "user", text: input }]);

        // -----------------------------
        // STEP 1 ‚Äì select document type
        // -----------------------------
        if (!selectedDoc) {
            const type = input.toLowerCase();

            if (!DOCUMENT_TYPES[type]) {
                setMessages(prev => [
                    ...prev,
                    { sender: "bot", text: "Te rog alege: cerere, contract sau procura." }
                ]);
                setInput("");
                return;
            }

            setSelectedDoc(type);
            setMessages(prev => [
                ...prev,
                { sender: "bot", text: DOCUMENT_TYPES[type].questions[0] }
            ]);
            setInput("");
            return;
        }

        // -----------------------------
        // STEP 2 ‚Äì collect data
        // -----------------------------
        const doc = DOCUMENT_TYPES[selectedDoc];
        const field = doc.fields[currentStep];

        const updatedFormData = {
            ...formData,
            [field]: input
        };

        setFormData(updatedFormData);

        if (currentStep < doc.questions.length - 1) {
            setMessages(prev => [
                ...prev,
                { sender: "bot", text: doc.questions[currentStep + 1] }
            ]);
            setCurrentStep(prev => prev + 1);
        } else {
            // -----------------------------
            // STEP 3 ‚Äì FINAL ‚Üí generate DOCX
            // -----------------------------
            setIsLoading(true);
            setMessages(prev => [
                ...prev,
                { sender: "bot", text: "Generez documentul, te rog a»ôteaptƒÉ..." }
            ]);

            const payload = {
                ...updatedFormData,
                DocumentType: selectedDoc
            };

            try {
                const blob = await messageService.sendMessage(payload);

                setMessages(prev => [
                    ...prev,
                    {
                        sender: "bot",
                        text: "üìÑ Documentul este gata",
                        fileBlob: blob
                    },
                    { sender: "bot", text: "Dore»ôti sƒÉ generezi alt document?" }
                ]);
            } catch (err) {
                setMessages(prev => [
                    ...prev,
                    { sender: "bot", text: "A apƒÉrut o eroare la generare ‚ùå" }
                ]);
            }

            // reset state
            setSelectedDoc(null);
            setCurrentStep(0);
            setFormData({});
            setIsLoading(false);
        }

        setInput("");
    };

    return (
        <div style={{ height: "100%", display: "flex", flexDirection: "column" }}>
            <div style={{ flex: 1, overflowY: "auto", padding: 20 }}>
                {messages.map((msg, i) => (
                    <div key={i} style={{ textAlign: msg.sender === "user" ? "right" : "left" }}>
                        <div
                            style={{
                                display: "inline-block",
                                padding: 12,
                                margin: 6,
                                borderRadius: 10,
                                background: msg.sender === "user" ? "#6c63ff" : "#e6e6e6",
                                color: msg.sender === "user" ? "white" : "black"
                            }}
                        >
                            {msg.text}

                            {msg.fileBlob && (
                                <div style={{ marginTop: 8 }}>
                                    <button onClick={() => downloadDocx(msg.fileBlob)}>
                                        ‚¨áÔ∏è DescarcƒÉ documentul
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>
                ))}

                {isLoading && (
                    <div style={{ textAlign: "left", color: "#999", margin: 6 }}>
                        Bot scrie...
                    </div>
                )}

                <div ref={messagesEndRef} />
            </div>

            <div style={{ display: "flex", padding: 10 }}>
                <input
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                    style={{ flex: 1, padding: 10 }}
                    placeholder="Scrie rƒÉspunsul..."
                    disabled={isLoading}
                />
                <button onClick={sendMessage} style={{ marginLeft: 10 }} disabled={isLoading}>
                    Trimite
                </button>
            </div>
        </div>
    );
};

export default ChatUI;
