import React, { useState, useRef, useEffect } from "react";
import { messageService } from "./messageService";

const ChatUI = () => {
    const [messages, setMessages] = useState([
        { sender: "bot", text: "Bun venit! Ce tip de cerere dorești să generezi?" }
    ]);

    const [input, setInput] = useState("");
    const messagesEndRef = useRef(null);

    // conversation steps
    const questions = [
        "Ce tip de cerere dorești să generezi?",
        "Cine sunt părțile implicate?",
        "Care este contextul sau scopul cererii?",
        "Există un conflict juridic?",
        "Care sunt argumentele sau probele invocate?",
        "Care este termenul dorit?",
        "Ce ton dorești să aibă cererea? (formal, strict formal etc.)"
    ];

    const fieldNames = [
        "TipCerere",
        "PartiImplicate",
        "Context",
        "Conflict",
        "Argumente",
        "Termen",
        "Ton"
    ];

    const [conversationStep, setConversationStep] = useState(0);

    const [formData, setFormData] = useState({
        TipCerere: "",
        PartiImplicate: "",
        Context: "",
        Conflict: "",
        Argumente: "",
        Termen: "",
        Ton: ""
    });

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const sendMessage = async () => {
        if (!input.trim()) return;

        const userMsg = { sender: "user", text: input };
        setMessages((prev) => [...prev, userMsg]);

        // Store answer
        const currentField = fieldNames[conversationStep];

        setFormData((prev) => ({
            ...prev,
            [currentField]: input
        }));

        // NEXT STEP OR FINAL STEP
        if (conversationStep < questions.length - 1) {
            // Ask next question
            const nextQuestion = questions[conversationStep + 1];
            setMessages((prev) => [...prev, { sender: "bot", text: nextQuestion }]);
            setConversationStep((prev) => prev + 1);
        } else {
            // All fields collected → send to backend
            const fullPayload = {
                ...formData,
                [currentField]: input
            };

            const response = await messageService.sendMessage(fullPayload);
            setMessages((prev) => [...prev, response]);

            // Reset conversation
            setConversationStep(0);
            setFormData({
                TipCerere: "",
                PartiImplicate: "",
                Context: "",
                Conflict: "",
                Argumente: "",
                Termen: "",
                Ton: ""
            });

            setMessages((prev) => [
                ...prev,
                { sender: "bot", text: "Dacă dorești să generezi altă cerere, spune-mi tipul cererii." }
            ]);
        }

        setInput("");
    };

    return (
        <div style={{
            display: "flex",
            flexDirection: "column",
            height: "100vh",
            width: "100%",
            fontFamily: "Arial"
        }}>
            <div style={{ flex: 1, overflowY: "auto", padding: "20px" }}>
                {messages.map((msg, i) => (
                    <div
                        key={i}
                        style={{
                            display: "flex",
                            justifyContent: msg.sender === "user" ? "flex-end" : "flex-start",
                            marginBottom: "10px"
                        }}
                    >
                        <div
                            style={{
                                padding: "10px",
                                borderRadius: "10px",
                                background: msg.sender === "user" ? "#007bff" : "#e6e6e6",
                                color: msg.sender === "user" ? "white" : "black",
                                maxWidth: "70%"
                            }}
                        >
                            {msg.text}
                        </div>
                    </div>
                ))}
                <div ref={messagesEndRef} />
            </div>

            <div style={{ padding: "10px", display: "flex", borderTop: "1px solid #ddd" }}>
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                    placeholder="Scrie un mesaj..."
                    style={{ flex: 1, marginRight: "10px", padding: "10px" }}
                />
                <button onClick={sendMessage} style={{ padding: "10px 20px" }}>
                    Trimite
                </button>
            </div>
        </div>
    );
};

export default ChatUI;
