import React, { useState } from 'react';
import { registerUser } from '../AuthService';

const SignUp = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        try {
            await registerUser(email, password, firstName, lastName, 'client');

            setEmail('');
            setPassword('');
            setFirstName('');
            setLastName('');

            alert("Înregistrare finalizată cu succes!");
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div
            style={{
                backgroundColor: '#fff',
                padding: '30px',
                borderRadius: '10px',
                maxWidth: '400px',
                width: '100%',
                margin: '0 auto',
                boxShadow: '0px 4px 15px rgba(0,0,0,0.1)',
                boxSizing: 'border-box'
            }}
        >
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>
                Creează un cont nou
            </h2>

            {error && (
                <p style={{
                    color: 'red',
                    fontWeight: 'bold',
                    textAlign: 'center',
                    marginBottom: '15px'
                }}>
                    {error}
                </p>
            )}

            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Prenume"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    required
                    style={inputStyle}
                />

                <input
                    type="text"
                    placeholder="Nume"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    required
                    style={inputStyle}
                />

                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    style={inputStyle}
                />

                <input
                    type="password"
                    placeholder="Parolă (min. 6 caractere)"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    style={inputStyle}
                />

                <button
                    type="submit"
                    disabled={loading}
                    style={{
                        width: '100%',
                        backgroundColor: '#28a745',
                        border: 'none',
                        padding: '12px',
                        color: 'white',
                        fontSize: '16px',
                        borderRadius: '6px',
                        cursor: 'pointer',
                        marginTop: '10px'
                    }}
                >
                    {loading ? 'Se procesează...' : 'Creează cont'}
                </button>
            </form>
        </div>
    );
};

const inputStyle = {
    width: '100%',
    padding: '12px',
    marginBottom: '15px',
    borderRadius: '6px',
    border: '1px solid #ccc',
    fontSize: '16px',
    boxSizing: 'border-box'
};

export default SignUp;
