import React, { useState } from "react";
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Navigate,
    useNavigate
} from "react-router-dom";

import Login from "./components/LogIn";
import SignUp from "./components/SignUp";
import ChatUI from "./components/ChatUI";
import DocumentePrestabilite from "./components/DocumentsUI";

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = () => {
        setIsLoggedIn(true);
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
    };

    return (
        <Router>
            <MainLayout
                isLoggedIn={isLoggedIn}
                onLogout={handleLogout}
            />

            <Routes>

                {/* Pagina principalÄƒ */}
                <Route path="/" element={<DocumentePrestabilite />} />

                {/* LOGIN */}
                <Route
                    path="/login"
                    element={<Login onLogin={handleLogin} />}
                />

                {/* SIGN UP */}
                <Route
                    path="/signup"
                    element={<SignUp onSignUp={handleLogin} />}
                />

                {/* CHAT â€” rute protejate */}
                <Route
                    path="/chat"
                    element={
                        isLoggedIn ? <ChatUI /> : <Navigate to="/login" />
                    }
                />
            </Routes>
        </Router>
    );
}

export default App;


function MainLayout({ isLoggedIn, onLogout }) {
    const navigate = useNavigate();

    return (
        <header
            style={{
                width: "100%",
                maxWidth: "1200px",
                margin: "0 auto",
                padding: "15px 20px",
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                flexWrap: "wrap", // permite butoanelor sÄƒ se mute pe rÃ¢nd dacÄƒ e nevoie
                boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
                boxSizing: "border-box",
                position: "relative", // pentru ca butonul de chat sÄƒ fie fix relativ la viewport
            }}
        >
            {/* Logo / Titlu */}
            <h2
                style={{ cursor: "pointer", margin: "5px 0" }}
                onClick={() => navigate("/")}
            >
                AplicaÈ›ia Mea Firebase/React
            </h2>

            {/* Butoane Log In / Log Out */}
            <div style={{ display: "flex", gap: "10px", margin: "5px 0" }}>
                {!isLoggedIn ? (
                    <button
                        onClick={() => navigate("/login")}
                        style={{
                            backgroundColor: "#007bff",
                            border: "none",
                            padding: "8px 16px",
                            color: "white",
                            borderRadius: "6px",
                            cursor: "pointer",
                            minWidth: "80px",
                        }}
                    >
                        Log In
                    </button>
                ) : (
                    <button
                        onClick={() => {
                            onLogout();
                            navigate("/");
                        }}
                        style={{
                            backgroundColor: "#dc3545",
                            border: "none",
                            padding: "8px 16px",
                            color: "white",
                            borderRadius: "6px",
                            cursor: "pointer",
                            minWidth: "80px",
                        }}
                    >
                        Log Out
                    </button>
                )}
            </div>

            {/* BUTON CHAT (AI) */}
            <button
                onClick={() => {
                    if (!isLoggedIn) {
                        navigate("/login");
                    } else {
                        navigate("/chat");
                    }
                }}
                style={{
                    position: "fixed",
                    bottom: "25px",
                    right: "25px",
                    background: "#6c63ff",
                    border: "none",
                    width: "70px",
                    height: "70px",
                    borderRadius: "50%",
                    color: "white",
                    fontSize: "28px",
                    cursor: "pointer",
                    zIndex: 9999,
                    boxShadow: "0 0 20px rgba(108,99,255,0.8)",
                    transition: "0.25s ease",
                }}
                onMouseOver={(e) => {
                    e.target.style.transform = "scale(1.12)";
                    e.target.style.boxShadow = "0 0 25px rgba(108,99,255,1)";
                }}
                onMouseOut={(e) => {
                    e.target.style.transform = "scale(1)";
                    e.target.style.boxShadow = "0 0 20px rgba(108,99,255,0.8)";
                }}
            >
                ðŸ¤–
            </button>
        </header>
    );
}
