import React, { useEffect, useRef, useState } from "react";
import { ChatFlow } from "./ChatFlow";
import { DOCUMENT_TYPES } from "./models/documentTypes";
import { downloadDocx } from "./fileUtils";
import { useAuth } from "../../auth/AuthContext";
import { useChatHistory } from "../../hooks/useChatHistory";
import ChatHistorySidebar from "./ChatHistorySidebar";

// Create ONE flow instance (acts like a controller)
const chatFlow = new ChatFlow(DOCUMENT_TYPES);

const ChatUI = ({ messageService }) => {
    const { user } = useAuth();
    const { 
        conversations, 
        messages: historyMessages, 
        loading: historyLoading, 
        saveMessage, 
        startNewConversation, 
        selectConversation,
        deleteConversation
    } = useChatHistory(user?.uid);

    const [localMessages, setLocalMessages] = useState([
        {
            sender: "bot",
            text: "üëã Salut! Ce tip de document dore»ôti sƒÉ generezi?\n(cerere / contract / procura)"
        }
    ]);

    const [input, setInput] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [isHistoryOpen, setIsHistoryOpen] = useState(false);

    const messagesEndRef = useRef(null);

    // If historyMessages are loaded (meaning a conversation is selected), use them.
    // Otherwise, use localMessages (for new/unauthenticated sessions).
    const messages = (user && historyMessages.length > 0) ? historyMessages : localMessages;

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages, isLoading]);

    const handleNewConversation = () => {
        startNewConversation();
        setLocalMessages([{
            sender: "bot",
            text: "üëã Salut! Ce tip de document dore»ôti sƒÉ generezi?\n(cerere / contract / procura)"
        }]);
        chatFlow.reset();
        setIsHistoryOpen(false); // Optional: close sidebar on new chat
    };

    const handleSelectConversation = (id) => {
        selectConversation(id);
        // We don't need to set localMessages because 'messages' will switch to 'historyMessages'
        setIsHistoryOpen(false); // Close sidebar on selection
    };

    const addMsg = (msg) => {
        // Always update local view for immediate feedback
        setLocalMessages(prev => [...prev, msg]);
        
        // If logged in, save to history (which will eventually update 'historyMessages')
        if (user) {
            saveMessage(msg);
        }
    };

    const sendMessage = async () => {
        if (!input.trim() || isLoading) return;

        const userInput = input.trim();
        setInput("");

        // show user message
        addMsg({ sender: "user", text: userInput });

        try {
            // STEP 1 ‚Äì select document
            if (!chatFlow.isDocumentSelected()) {
                const question = chatFlow.selectDocument(userInput.toLowerCase());

                addMsg({ sender: "bot", text: question });
                return;
            }

            // STEP 2 ‚Äì collect answers
            const result = chatFlow.handleAnswer(userInput);

            if (!result.done) {
                addMsg({ sender: "bot", text: result.nextQuestion });
                return;
            }

            // STEP 3 ‚Äì generate document
            setIsLoading(true);
            
            const blob = await messageService.sendMessage(result.payload);

            addMsg({
                sender: "bot",
                text: "üìÑ Documentul este gata!",
                fileBlob: blob
            });
            
            addMsg({
                sender: "bot",
                text: "Vrei sƒÉ generezi un alt document?"
            });

            chatFlow.reset();
        } catch (err) {
            console.error(err);
            addMsg({ sender: "bot", text: "‚ùå A apƒÉrut o eroare. Te rog √ÆncearcƒÉ din nou." });
            chatFlow.reset();
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div style={styles.wrapper}>
            <style>{`
                .history-btn {
                    background: transparent;
                    border: none;
                    cursor: pointer;
                    color: #6B7280;
                    font-size: 14px;
                    font-weight: 500;
                    display: flex;
                    align-items: center;
                    gap: 8px;
                    padding: 8px 12px;
                    border-radius: 8px;
                    transition: all 0.2s ease;
                }
                .history-btn:hover {
                    background-color: #F3F4F6;
                    color: #4F46E5;
                }
                .history-btn.active {
                    color: #4F46E5;
                    background-color: #EEF2FF;
                }
                .history-icon {
                    width: 18px;
                    height: 18px;
                    stroke-width: 2;
                }
            `}</style>

            <div style={styles.chatContainer}>
                {/* Header with History Button */}
                {user && (
                    <div style={styles.header}>
                        <button 
                            onClick={() => setIsHistoryOpen(!isHistoryOpen)}
                            className={`history-btn ${isHistoryOpen ? 'active' : ''}`}
                        >
                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="history-icon">
                                <path strokeLinecap="round" strokeLinejoin="round" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                            </svg>
                            <span>Istoric</span>
                        </button>
                    </div>
                )}

                <div style={styles.messages}>
                    {messages.map((msg, i) => (
                        <div
                            key={i}
                            style={{
                                display: "flex",
                                justifyContent:
                                    msg.sender === "user" ? "flex-end" : "flex-start",
                                marginBottom: 10
                            }}
                        >
                            <div
                                style={{
                                    ...styles.bubble,
                                    background:
                                        msg.sender === "user" ? "#6c63ff" : "#f2f2f2",
                                    color: msg.sender === "user" ? "#fff" : "#000"
                                }}
                            >
                                {msg.text}

                                {(msg.fileBlob || msg.fileUrl) && (
                                    <div style={{ marginTop: 10 }}>
                                        <button
                                            style={styles.downloadBtn}
                                            onClick={() => {
                                                if (msg.fileBlob) {
                                                    downloadDocx(msg.fileBlob, "document.docx");
                                                } else if (msg.fileUrl) {
                                                    window.open(msg.fileUrl, "_blank");
                                                }
                                            }}
                                        >
                                            ‚¨áÔ∏è DescarcƒÉ documentul
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    ))}

                    {isLoading && (
                        <div style={styles.typing}>Bot scrie...</div>
                    )}

                    <div ref={messagesEndRef} />
                </div>

                {/* INPUT BAR */}
                <div style={styles.inputBar}>
                    <input
                        value={input}
                        onChange={e => setInput(e.target.value)}
                        onKeyDown={e => e.key === "Enter" && sendMessage()}
                        placeholder="Scrie rƒÉspunsul..."
                        style={styles.input}
                        disabled={isLoading}
                    />
                    <button
                        onClick={sendMessage}
                        style={styles.sendBtn}
                        disabled={isLoading}
                    >
                        Trimite
                    </button>
                </div>
            </div>

            <ChatHistorySidebar 
                isOpen={isHistoryOpen} 
                onClose={() => setIsHistoryOpen(false)} 
                conversations={conversations}
                onSelectConversation={handleSelectConversation}
                onNewConversation={handleNewConversation}
                onDeleteConversation={deleteConversation}
                loading={historyLoading}
            />
        </div>
    );
};

export default ChatUI;

const styles = {
    wrapper: {
        height: "100%",
        display: "flex",
        position: "relative",
        overflow: "hidden"
    },

    chatContainer: {
        flex: 1,
        height: "100%",
        display: "flex",
        flexDirection: "column",
        background: "#fff",
        position: "relative"
    },

    header: {
        padding: "12px 24px",
        display: "flex",
        justifyContent: "flex-end",
        background: "#fff", 
    },

    messages: {
        flex: 1,
        padding: 20,
        overflowY: "auto"
    },

    bubble: {
        maxWidth: "70%",
        padding: 14,
        borderRadius: 16,
        whiteSpace: "pre-wrap"
    },

    typing: {
        fontSize: 12,
        color: "#999",
        marginTop: 6
    },

    inputBar: {
        display: "flex",
        gap: 10,
        padding: 14,
        borderTop: "1px solid #eee",
        background: "#fff"
    },

    input: {
        flex: 1,
        padding: 12,
        borderRadius: 10,
        border: "1px solid #ddd",
        outline: "none"
    },

    sendBtn: {
        padding: "0 20px",
        borderRadius: 10,
        border: "none",
        background: "#6c63ff",
        color: "#fff",
        cursor: "pointer"
    },

    downloadBtn: {
        marginTop: 4,
        padding: "6px 10px",
        borderRadius: 6,
        border: "none",
        cursor: "pointer"
    }
};
