// src/components/DocumentePrestabilite.jsx

import React, { useState } from 'react';
import { useDocumentData } from '../hooks/useDocumentData';
import { DocumentCard } from './DocumentCard';
import { DocumentModal } from './DocumentModal';

const categorii = ["Toate", "Contracte", "Cereri", "Penal", "Civil"];

const DocumentePrestabilite = () => {
    const { documents, loading, error } = useDocumentData();

    const [categorieSelectata, setCategorieSelectata] = useState("Toate");
    const [documentIdToOpen, setDocumentIdToOpen] = useState(null);

    const filtrate = categorieSelectata === "Toate"
        ? documents
        : documents.filter(d => d.categorie === categorieSelectata);

    const handleOpenDocument = (docId) => {
        const documentToOpen = documents.find(doc => doc.id === docId);

        if (!documentToOpen) return;

        // 1. PDF – Deschidere directă
        if (documentToOpen.type === "pdf" && documentToOpen.url) {
            window.open(documentToOpen.url, "_blank");
            return;
        }

        // 2. Document text/HTML – Deschidem modalul
        if (documentToOpen.continut) {
            setDocumentIdToOpen(docId);
            return;
        }

        // 3. Document invalid
        alert("Acest document nu are nici URL de PDF, nici conținut text.");
        console.warn("Document invalid:", documentToOpen);
    };

    const handleCloseModal = () => {
        setDocumentIdToOpen(null);
    };

    if (loading) return <p style={{ padding: 20 }}>Se încarcă documentele...</p>;
    if (error) return <p style={{ padding: 20, color: "red" }}>Eroare: {error}</p>;

    return (
        <div style={{ padding: "20px" }}>
            <h1 style={{ fontSize: "28px", marginBottom: "20px", fontWeight: "bold" }}>
                Documente Prestabilite
            </h1>

            {/* FILTRE */}
            <div style={{ marginBottom: "20px", display: "flex", gap: "10px", flexWrap: "wrap" }}>
                {categorii.map(cat => (
                    <button
                        key={cat}
                        onClick={() => setCategorieSelectata(cat)}
                        style={{
                            padding: "10px 15px",
                            borderRadius: "8px",
                            border: "1px solid #ccc",
                            background: categorieSelectata === cat ? "#4e73df" : "white",
                            color: categorieSelectata === cat ? "white" : "black",
                            cursor: "pointer",
                            fontSize: "14px",
                            boxShadow: categorieSelectata === cat ? '0 2px 4px rgba(0,0,0,0.2)' : 'none'
                        }}
                    >
                        {cat}
                    </button>
                ))}
            </div>

            {/* GRID DOCUMENTE */}
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
                    gap: "20px",
                }}
            >
                {filtrate.map(doc => (
                    <DocumentCard
                        key={doc.id}
                        docData={doc}
                        onOpen={handleOpenDocument}
                    />
                ))}
            </div>

            {/* MODAL DOCUMENT TEXT */}
            {documentIdToOpen && (
                <DocumentModal
                    documentId={documentIdToOpen}
                    onClose={handleCloseModal}
                />
            )}
        </div>
    );
};

export default DocumentePrestabilite;
