import React, { useState } from 'react';

const categorii = ["Contracte", "Cereri", "Penal", "Civil"];

export const AddDocumentModal = ({ onClose, onSave }) => {
    const [title, setTitle] = useState("");
    const [category, setCategory] = useState(categorii[0]);
    const [type, setType] = useState("text"); // "text" or "pdf"
    const [content, setContent] = useState("");
    const [url, setUrl] = useState("");
    const [isSubmitting, setIsSubmitting] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        const newDoc = {
            titlu: title,
            categorie: category,
            type: type,
            ...(type === 'text' ? { continut: content } : { url: url }),
            dataAdaugarii: new Date().toISOString()
        };

        try {
            await onSave(newDoc);
            onClose();
        } catch (error) {
            alert("A apărut o eroare la salvarea documentului.");
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div style={styles.overlay}>
            <div style={styles.modal}>
                <div style={styles.header}>
                    <h2 style={styles.title}>Adaugă Document Nou</h2>
                    <button onClick={onClose} style={styles.closeButton}>&times;</button>
                </div>

                <form onSubmit={handleSubmit} style={styles.form}>
                    <div style={styles.formGroup}>
                        <label style={styles.label}>Titlu Document</label>
                        <input
                            type="text"
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                            required
                            style={styles.input}
                            placeholder="Ex: Contract de Vânzare-Cumpărare"
                        />
                    </div>

                    <div style={styles.formGroup}>
                        <label style={styles.label}>Categorie</label>
                        <select
                            value={category}
                            onChange={(e) => setCategory(e.target.value)}
                            style={styles.select}
                        >
                            {categorii.map(cat => (
                                <option key={cat} value={cat}>{cat}</option>
                            ))}
                        </select>
                    </div>

                    <div style={styles.formGroup}>
                        <label style={styles.label}>Tip Document</label>
                        <div style={styles.radioGroup}>
                            <label style={styles.radioLabel}>
                                <input
                                    type="radio"
                                    value="text"
                                    checked={type === "text"}
                                    onChange={() => setType("text")}
                                /> Text
                            </label>
                            <label style={styles.radioLabel}>
                                <input
                                    type="radio"
                                    value="pdf"
                                    checked={type === "pdf"}
                                    onChange={() => setType("pdf")}
                                /> PDF (URL)
                            </label>
                        </div>
                    </div>

                    {type === "text" ? (
                        <div style={styles.formGroup}>
                            <label style={styles.label}>Conținut</label>
                            <textarea
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                                required
                                style={styles.textarea}
                                placeholder="Introduceți conținutul documentului..."
                            />
                        </div>
                    ) : (
                        <div style={styles.formGroup}>
                            <label style={styles.label}>URL PDF</label>
                            <input
                                type="url"
                                value={url}
                                onChange={(e) => setUrl(e.target.value)}
                                required
                                style={styles.input}
                                placeholder="https://example.com/document.pdf"
                            />
                        </div>
                    )}

                    <div style={styles.footer}>
                        <button type="button" onClick={onClose} style={styles.cancelButton}>
                            Anulează
                        </button>
                        <button
                            type="submit"
                            disabled={isSubmitting}
                            style={{
                                ...styles.saveButton,
                                opacity: isSubmitting ? 0.7 : 1
                            }}
                        >
                            {isSubmitting ? "Se salvează..." : "Salvează Document"}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

const styles = {
    overlay: {
        position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        display: 'flex', alignItems: 'center', justifyContent: 'center',
        zIndex: 1000, padding: '20px'
    },
    modal: {
        backgroundColor: 'white',
        borderRadius: '12px',
        width: '100%',
        maxWidth: '600px',
        maxHeight: '90vh',
        overflowY: 'auto',
        boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)'
    },
    header: {
        padding: '20px 24px',
        borderBottom: '1px solid #E5E7EB',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
    },
    title: { margin: 0, fontSize: '20px', fontWeight: '600', color: '#111827' },
    closeButton: {
        background: 'none', border: 'none', fontSize: '24px',
        color: '#6B7280', cursor: 'pointer', padding: '0 4px'
    },
    form: { padding: '24px' },
    formGroup: { marginBottom: '20px' },
    label: { display: 'block', marginBottom: '8px', fontSize: '14px', fontWeight: '500', color: '#374151' },
    input: {
        width: '100%', padding: '10px 12px', borderRadius: '6px',
        border: '1px solid #D1D5DB', fontSize: '14px', boxSizing: 'border-box'
    },
    select: {
        width: '100%', padding: '10px 12px', borderRadius: '6px',
        border: '1px solid #D1D5DB', fontSize: '14px', backgroundColor: 'white', boxSizing: 'border-box'
    },
    textarea: {
        width: '100%', padding: '10px 12px', borderRadius: '6px',
        border: '1px solid #D1D5DB', fontSize: '14px', minHeight: '150px',
        resize: 'vertical', boxSizing: 'border-box', fontFamily: 'inherit'
    },
    radioGroup: { display: 'flex', gap: '20px' },
    radioLabel: { display: 'flex', alignItems: 'center', gap: '6px', fontSize: '14px', cursor: 'pointer' },
    footer: {
        display: 'flex', justifyContent: 'flex-end', gap: '12px', marginTop: '32px'
    },
    cancelButton: {
        padding: '10px 20px', borderRadius: '6px', border: '1px solid #D1D5DB',
        backgroundColor: 'white', color: '#374151', fontSize: '14px', fontWeight: '500',
        cursor: 'pointer'
    },
    saveButton: {
        padding: '10px 20px', borderRadius: '6px', border: 'none',
        backgroundColor: '#4F46E5', color: 'white', fontSize: '14px', fontWeight: '500',
        cursor: 'pointer'
    }
};
