// src/components/DocumentePrestabilite.jsx

import React, { useState } from 'react';
import { useDocumentData } from '../hooks/useDocumentData';
import { DocumentCard } from './DocumentCard'; // Componenta Card
import { DocumentModal } from './DocumentModal'; // Componenta Modal

// Categoriile rămân aceleași pentru butoanele de filtrare
const categorii = ["Toate", "Contracte", "Cereri", "Penal", "Civil"];

const DocumentePrestabilite = () => {
    // 1. Preluarea datelor prin Custom Hook
    const { documents, loading, error } = useDocumentData();

    // Stări de filtrare și Modal (Coordonare)
    const [categorieSelectata, setCategorieSelectata] = useState("Toate");
    const [documentIdToOpen, setDocumentIdToOpen] = useState(null); // ID-ul de deschis

    // 2. LOGICA DE FILTRARE
    const filtrate = categorieSelectata === "Toate"
        ? documents
        : documents.filter(d => d.categorie === categorieSelectata);

    // 3. FUNCTII DE COORDONARE
    const handleOpenDocument = (docId) => {
        const documentToOpen = documents.find(doc => doc.id === docId);

        if (!documentToOpen) return;

        // 1. VERIFICĂM DACĂ ESTE PDF (Aceasta trebuie să fie prioritatea)
        if (documentToOpen.type === 'pdf' && documentToOpen.url) {
            // Dacă este PDF și are URL, deschidem direct în filă nouă
            window.open(documentToOpen.url, '_blank');

            // FOARTE IMPORTANT: NU MAI FACEM NIMIC ALTCEVA
            return;
        }

        // 2. LOGICĂ DE REZERVĂ PENTRU CONȚINUT HTML/TEXT

        // Dacă documentul nu este PDF, și are un câmp 'continut' (sau este implicit text),
        // deschidem modalul pentru a prelua conținutul.

        // Dacă documentul este un model nou care nu are nici 'type: pdf' și nici 'continut'
        // (sau este un tip necunoscut), cel mai bine este să NU deschidem nimic.

        if (documentToOpen.continut) { // Verificăm explicit dacă documentul e gândit să aibă conținut
            setDocumentIdToOpen(docId);
        } else {
            console.warn("Acest document nu este un PDF și nu are conținut HTML/text definit.", documentToOpen);
            // Puteți adăuga un alert aici: alert("Tip de document necunoscut sau conținut lipsă.");
        }
    };

    const handleCloseModal = () => {
        setDocumentIdToOpen(null);
    };

    // 4. AFIȘAREA STĂRII DE ÎNCĂRCARE SAU EROARE
    if (loading) {
        return <p style={{ padding: "20px", fontSize: "18px" }}>Se încarcă modelele de documente...</p>;
    }

    if (error) {
        return <p style={{ padding: "20px", fontSize: "18px", color: "red" }}>Eroare: {error}</p>;
    }


    return (
        <div style={{ padding: "20px" }}>
            <h1 style={{ fontSize: "28px", marginBottom: "20px", fontWeight: "bold" }}>
                Documente Prestabilite
            </h1>

            {/* FILTRE */}
            <div style={{ marginBottom: "20px", display: "flex", gap: "10px", flexWrap: "wrap" }}>
                {categorii.map(cat => (
                    <button
                        key={cat}
                        onClick={() => setCategorieSelectata(cat)}
                        style={{
                            padding: "10px 15px",
                            borderRadius: "8px", // Marginile rotunjite
                            border: "1px solid #ccc", // Contur subțire
                            // Logica de culoare pentru butonul selectat
                            background: categorieSelectata === cat ? "#4e73df" : "white",
                            color: categorieSelectata === cat ? "white" : "black",
                            cursor: "pointer",
                            fontWeight: "normal",
                            // Stiluri adăugate pentru a arăta ca 'tag-uri' mici:
                            fontSize: "14px",
                            height: "fit-content", // Ajustează înălțimea
                            boxShadow: categorieSelectata === cat ? '0 2px 4px rgba(0,0,0,0.2)' : 'none'
                        }}
                    >
                        {cat}
                    </button>
                ))}
            </div>

            {/* GRID DOCUMENTE */}
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
                    gap: "20px",
                }}
            >
                {filtrate.map(doc => (
                    <DocumentCard
                        key={doc.id}
                        docData={doc}
                        onOpen={handleOpenDocument} // Transmitem funcția de deschidere
                    />
                ))}
            </div>

            {/* MODALUL - Se randează doar dacă avem un ID de deschis */}
            {documentIdToOpen && (
                <DocumentModal
                    documentId={documentIdToOpen}
                    onClose={handleCloseModal} // Transmitem funcția de închidere
                />
            )}
        </div>
    );
};

export default DocumentePrestabilite;
