import React, { useState } from "react";
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Navigate,
    useNavigate
} from "react-router-dom";

import Login from "./components/LogIn";
import SignUp from "./components/SignUp";
import ChatUI from "./components/ChatUI";
import DocumentePrestabilite from "./components/DocumentsUI";

function App() {
    // Citim starea iniÈ›ialÄƒ din localStorage
    const [isLoggedIn, setIsLoggedIn] = useState(
        () => localStorage.getItem("isLoggedIn") === "true"
    );

    const handleLogin = () => {
        setIsLoggedIn(true);
        localStorage.setItem("isLoggedIn", "true");
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        localStorage.removeItem("isLoggedIn");
    };

    return (
        <Router>
            <Routes>
                <Route
                    path="/"
                    element={
                        <MainLayout isLoggedIn={isLoggedIn} onLogout={handleLogout}>
                            <DocumentePrestabilite />
                        </MainLayout>
                    }
                />
                <Route
                    path="/login"
                    element={
                        <MainLayout isLoggedIn={isLoggedIn} onLogout={handleLogout}>
                            <Login onLogin={handleLogin} />
                        </MainLayout>
                    }
                />
                <Route
                    path="/signup"
                    element={
                        <MainLayout isLoggedIn={isLoggedIn} onLogout={handleLogout}>
                            <SignUp onSignUp={handleLogin} />
                        </MainLayout>
                    }
                />
                <Route
                    path="/chat"
                    element={
                        <MainLayout isLoggedIn={isLoggedIn} onLogout={handleLogout}>
                            <ChatUI /> {/* FÄƒrÄƒ verificare de autentificare */}
                        </MainLayout>
                    }
                />
            </Routes>
        </Router>
    );
}

export default App;

// ================= MainLayout =================
function MainLayout({ isLoggedIn, onLogout, children }) {
    const navigate = useNavigate();

    // ObÈ›inem ruta curentÄƒ
    const currentPath = window.location.pathname;

    return (
        <div>
            <header
                style={{
                    width: "100%",
                    margin: "0 auto",
                    padding: "20px 30px",
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    flexWrap: "wrap",
                    boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
                    boxSizing: "border-box",
                    position: "relative",
                    borderRadius: "8px",
                    backgroundColor: "#fff",
                }}
            >
                <h2
                    style={{ cursor: "pointer", margin: "5px 0" }}
                    onClick={() => navigate("/")}
                >
                    AplicaÈ›ia Mea Firebase/React
                </h2>

                <div style={{ display: "flex", gap: "10px", margin: "5px 0" }}>
                    {!isLoggedIn ? (
                        <button
                            onClick={() => navigate("/login")}
                            style={buttonStyle("#007bff")}
                        >
                            Log In
                        </button>
                    ) : (
                        <button
                            onClick={() => {
                                onLogout();
                                navigate("/");
                            }}
                            style={buttonStyle("#dc3545")}
                        >
                            Log Out
                        </button>
                    )}
                </div>
            </header>

            <main style={{ padding: "20px" }}>{children}</main>

            {/* Buton fix jos-dreapta */}
            {currentPath === "/chat" ? (
                <button
                    onClick={() => navigate("/")}
                    style={floatingButtonStyle("#28a745", true)}
                    onMouseOver={(e) => handleMouseOver(e, "#28a745")}
                    onMouseOut={(e) => handleMouseOut(e, "#28a745")}
                >
                    ðŸ“„
                </button>
            ) : (
                <button
                    onClick={() => navigate("/chat")}
                    style={floatingButtonStyle("#6c63ff")}
                    onMouseOver={(e) => handleMouseOver(e, "#6c63ff")}
                    onMouseOut={(e) => handleMouseOut(e, "#6c63ff")}
                >
                    ðŸ¤–
                </button>

            )}
        </div>
    );
}

// Stiluri reutilizabile
const buttonStyle = (bgColor) => ({
    backgroundColor: bgColor,
    border: "none",
    padding: "8px 16px",
    color: "white",
    borderRadius: "6px",
    cursor: "pointer",
    minWidth: "80px",
});

const floatingButtonStyle = (bgColor, isDocumentButton = false) => ({
    position: "fixed",
    bottom: isDocumentButton ? "90px" : "25px",
    right: "25px",
    background: `linear-gradient(135deg, ${bgColor}, #ffffff33)`, // gradient subtil
    border: "none",
    width: "55px",
    height: "55px",
    borderRadius: "50%",
    color: "white",
    fontSize: "22px",
    cursor: "pointer",
    zIndex: 9999,
    boxShadow: "0 6px 15px rgba(0,0,0,0.25)", // umbra mai soft
    transition: "0.3s ease, transform 0.2s ease",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
});

// Hover styling cu transform È™i glow
const handleMouseOver = (e, bgColor) => {
    e.target.style.transform = "scale(1.15)";
    e.target.style.boxShadow = `0 8px 20px ${bgColor}88`; // glow uÈ™or
};

const handleMouseOut = (e, bgColor) => {
    e.target.style.transform = "scale(1)";
    e.target.style.boxShadow = "0 6px 15px rgba(0,0,0,0.25)";
};
