import React, { useState } from "react";

const ChatUI = () => {
    const [messages, setMessages] = useState([
        { sender: "bot", text: "Bun venit în chat! Cu ce te pot ajuta?" }
    ]);

    const [input, setInput] = useState("");

    const sendMessage = () => {
        if (!input.trim()) return;

        setMessages([...messages, { sender: "user", text: input }]);
        setInput("");
    };

    return (
        <div
            style={{
                display: "flex",
                flexDirection: "column",
                height: "80vh",
                width: "100%",
                maxWidth: "800px",
                margin: "40px auto",
                background: "#fff",
                borderRadius: "12px",
                boxShadow: "0px 4px 12px rgba(0,0,0,0.1)",
                overflow: "hidden"
            }}
        >
            {/* Header */}
            <div
                style={{
                    padding: "15px 20px",
                    background: "#007bff",
                    color: "white",
                    fontWeight: "bold",
                    fontSize: "18px"
                }}
            >
                Chat – Aplicația Mea
            </div>

            {/* Zona de mesaje */}
            <div
                style={{
                    flex: 1,
                    padding: "20px",
                    overflowY: "auto",
                    background: "#f7f7f7"
                }}
            >
                {messages.map((msg, index) => (
                    <div
                        key={index}
                        style={{
                            marginBottom: "15px",
                            display: "flex",
                            justifyContent:
                                msg.sender === "user" ? "flex-end" : "flex-start"
                        }}
                    >
                        <div
                            style={{
                                padding: "10px 15px",
                                borderRadius: "12px",
                                maxWidth: "70%",
                                background:
                                    msg.sender === "user" ? "#007bff" : "#e6e6e6",
                                color: msg.sender === "user" ? "white" : "black",
                            }}
                        >
                            {msg.text}
                        </div>
                    </div>
                ))}
            </div>

            {/* Input chat */}
            <div
                style={{
                    display: "flex",
                    padding: "15px",
                    background: "#fff",
                    borderTop: "1px solid #ddd"
                }}
            >
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Scrie un mesaj..."
                    style={{
                        flex: 1,
                        padding: "12px",
                        borderRadius: "8px",
                        border: "1px solid #ccc",
                        fontSize: "16px",
                        marginRight: "10px"
                    }}
                />

                <button
                    onClick={sendMessage}
                    style={{
                        padding: "12px 20px",
                        background: "#007bff",
                        color: "white",
                        border: "none",
                        borderRadius: "8px",
                        cursor: "pointer",
                        fontSize: "16px"
                    }}
                >
                    Trimite
                </button>
            </div>
        </div>
    );
};

export default ChatUI;
