import React, { useState } from 'react';
import { FileText, Gavel, BookOpen, Briefcase } from "lucide-react";

const documenteInitiale = [
    {
        id: 1,
        titlu: "Contract de Vânzare-Cumpărare",
        descriere: "Contract standard pentru vânzarea unui bun mobil.",
        categorie: "Contracte",
        icon: <Briefcase size={32} />,
    },
    {
        id: 2,
        titlu: "Cerere de Încetare a Contractului",
        descriere: "Model oficial pentru încetarea unui contract.",
        categorie: "Cereri",
        icon: <FileText size={32} />,
    },
    {
        id: 3,
        titlu: "Plângere Penală",
        descriere: "Model simplificat pentru sesizarea organelor competente.",
        categorie: "Penal",
        icon: <Gavel size={32} />,
    },
    {
        id: 4,
        titlu: "Cerere de Divorț",
        descriere: "Document necesar pentru declanșarea procesului de divorț.",
        categorie: "Civil",
        icon: <BookOpen size={32} />,
    }
];

const categorii = ["Toate", "Contracte", "Cereri", "Penal", "Civil"];

const DocumentePrestabilite = () => {
    const [categorieSelectata, setCategorieSelectata] = useState("Toate");

    const filtrate = categorieSelectata === "Toate"
        ? documenteInitiale
        : documenteInitiale.filter(d => d.categorie === categorieSelectata);

    return (
        <div style={{ padding: "20px" }}>
            <h1 style={{ fontSize: "28px", marginBottom: "20px", fontWeight: "bold" }}>
                Documente Prestabilite
            </h1>

            {/* FILTRE */}
            <div style={{ marginBottom: "20px", display: "flex", gap: "10px", flexWrap: "wrap" }}>
                {categorii.map(cat => (
                    <button
                        key={cat}
                        onClick={() => setCategorieSelectata(cat)}
                        style={{
                            padding: "10px 15px",
                            borderRadius: "8px",
                            border: "1px solid #ccc",
                            background: categorieSelectata === cat ? "#4e73df" : "white",
                            color: categorieSelectata === cat ? "white" : "black",
                            cursor: "pointer",
                        }}
                    >
                        {cat}
                    </button>
                ))}
            </div>

            {/* GRID DOCUMENTE */}
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
                    gap: "20px",
                }}
            >
                {filtrate.map(doc => (
                    <div
                        key={doc.id}
                        style={{
                            padding: "20px",
                            borderRadius: "12px",
                            boxShadow: "0 0 12px rgba(0,0,0,0.1)",
                            background: "white",
                            display: "flex",
                            flexDirection: "column",
                            justifyContent: "space-between"
                        }}
                    >
                        <div>
                            <div style={{ marginBottom: "10px" }}>{doc.icon}</div>
                            <h3 style={{ marginBottom: "10px", fontSize: "20px" }}>{doc.titlu}</h3>
                            <p style={{ fontSize: "14px", color: "#555" }}>{doc.descriere}</p>
                        </div>

                        <button
                            style={{
                                marginTop: "15px",
                                padding: "10px",
                                borderRadius: "8px",
                                background: "#4e73df",
                                color: "white",
                                border: "none",
                                cursor: "pointer",
                                fontWeight: "bold"
                            }}
                            onClick={() => alert(`Deschizi documentul: ${doc.titlu}`)}
                        >
                            Deschide Documentul
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default DocumentePrestabilite;
