import React, { useEffect, useRef, useState } from "react";

// -----------------------------
// Document definitions
// -----------------------------
const DOCUMENT_TYPES = {
    cerere: {
        fields: [
            "TipCerere",
            "PartiImplicate",
            "Context",
            "Conflict",
            "Argumente",
            "Termen",
            "Ton"
        ],
        questions: [
            "Ce tip de cerere dore»ôti sƒÉ generezi?",
            "Cine sunt pƒÉr»õile implicate?",
            "Care este contextul cererii?",
            "ExistƒÉ un conflict juridic?",
            "Argumente sau probe?",
            "Care este termenul dorit?",
            "Ce ton dore»ôti sƒÉ aibƒÉ cererea?"
        ]
    },
    contract: {
        fields: [
            "TipContract",
            "PartiContractante",
            "Obiect",
            "Context",
            "Obligatii",
            "Durata",
            "Pret",
            "ClauzeSpeciale",
            "Ton"
        ],
        questions: [
            "Ce tip de contract dore»ôti sƒÉ generezi?",
            "Cine sunt pƒÉr»õile contractului?",
            "Care este obiectul contractului?",
            "Care este contextul contractului?",
            "Care sunt obliga»õiile?",
            "Care este durata?",
            "Care este pre»õul?",
            "ExistƒÉ clauze speciale?",
            "Ce ton dore»ôti?"
        ]
    },
    procura: {
        fields: [
            "TipProcura",
            "Mandant",
            "Mandatar",
            "Obiect",
            "Context",
            "Puterile",
            "Durata",
            "ClauzeSpeciale",
            "Ton"
        ],
        questions: [
            "Ce tip de procurƒÉ dore»ôti?",
            "Cine este mandantul?",
            "Cine este mandatarul?",
            "Care este obiectul?",
            "Care este contextul?",
            "Ce drepturi se acordƒÉ?",
            "Care este durata?",
            "Clauze speciale?",
            "Ce ton dore»ôti?"
        ]
    }
};

const ChatUI = ({ messageService }) => {
    const [messages, setMessages] = useState([
        {
            sender: "bot",
            text: "üëã Salut! Ce tip de document dore»ôti sƒÉ generezi?\n(cerere / contract / procura)"
        }
    ]);

    const [input, setInput] = useState("");
    const [selectedDoc, setSelectedDoc] = useState(null);
    const [currentStep, setCurrentStep] = useState(0);
    const [formData, setFormData] = useState({});
    const [isLoading, setIsLoading] = useState(false);

    const messagesEndRef = useRef(null);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages, isLoading]);

    const downloadDocx = (blob) => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = `${selectedDoc || "document"}.docx`;
        a.click();
        window.URL.revokeObjectURL(url);
    };

    const sendMessage = async () => {
        if (!input.trim() || isLoading) return;

        setMessages((prev) => [...prev, { sender: "user", text: input }]);

        // STEP 1 ‚Äì select document type
        if (!selectedDoc) {
            const type = input.toLowerCase();

            if (!DOCUMENT_TYPES[type]) {
                setMessages((prev) => [
                    ...prev,
                    { sender: "bot", text: "‚ùó Te rog alege: cerere, contract sau procura." }
                ]);
                setInput("");
                return;
            }

            setSelectedDoc(type);
            setMessages((prev) => [
                ...prev,
                { sender: "bot", text: DOCUMENT_TYPES[type].questions[0] }
            ]);
            setInput("");
            return;
        }

        // STEP 2 ‚Äì collect data
        const doc = DOCUMENT_TYPES[selectedDoc];
        const field = doc.fields[currentStep];

        const updatedFormData = {
            ...formData,
            [field]: input
        };

        setFormData(updatedFormData);

        if (currentStep < doc.questions.length - 1) {
            setMessages((prev) => [
                ...prev,
                { sender: "bot", text: doc.questions[currentStep + 1] }
            ]);
            setCurrentStep((prev) => prev + 1);
        } else {
            // STEP 3 ‚Äì generate document
            setIsLoading(true);
            setMessages((prev) => [
                ...prev,
                { sender: "bot", text: "‚è≥ Generez documentul..." }
            ]);

            try {
                const blob = await messageService.sendMessage({
                    ...updatedFormData,
                    DocumentType: selectedDoc
                });

                setMessages((prev) => [
                    ...prev,
                    {
                        sender: "bot",
                        text: "üìÑ Documentul este gata!",
                        fileBlob: blob
                    },
                    { sender: "bot", text: "Vrei sƒÉ generezi un alt document?" }
                ]);
            } catch {
                setMessages((prev) => [
                    ...prev,
                    { sender: "bot", text: "‚ùå A apƒÉrut o eroare la generare." }
                ]);
            }

            // reset
            setSelectedDoc(null);
            setCurrentStep(0);
            setFormData({});
            setIsLoading(false);
        }

        setInput("");
    };

    return (
        <div style={styles.wrapper}>
            <div style={styles.chatContainer}>
                <div style={styles.messages}>
                    {messages.map((msg, i) => (
                        <div
                            key={i}
                            style={{
                                display: "flex",
                                justifyContent:
                                    msg.sender === "user" ? "flex-end" : "flex-start",
                                marginBottom: 10
                            }}
                        >
                            <div
                                style={{
                                    ...styles.bubble,
                                    background:
                                        msg.sender === "user" ? "#6c63ff" : "#f2f2f2",
                                    color: msg.sender === "user" ? "#fff" : "#000"
                                }}
                            >
                                {msg.text}

                                {msg.fileBlob && (
                                    <div style={{ marginTop: 10 }}>
                                        <button
                                            style={styles.downloadBtn}
                                            onClick={() => downloadDocx(msg.fileBlob)}
                                        >
                                            ‚¨áÔ∏è DescarcƒÉ documentul
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    ))}

                    {isLoading && (
                        <div style={styles.typing}>Bot scrie...</div>
                    )}

                    <div ref={messagesEndRef} />
                </div>

                {/* INPUT BAR */}
                <div style={styles.inputBar}>
                    <input
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                        placeholder="Scrie rƒÉspunsul..."
                        style={styles.input}
                        disabled={isLoading}
                    />
                    <button
                        onClick={sendMessage}
                        style={styles.sendBtn}
                        disabled={isLoading}
                    >
                        Trimite
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ChatUI;

// -----------------------------
// Styles
// -----------------------------
const styles = {
    wrapper: {
        height: "100%",
        display: "flex",
        justifyContent: "center",
        background: "#f7f8fc"
    },

    chatContainer: {
        width: "100%",
        maxWidth: 900,
        display: "flex",
        flexDirection: "column",
        background: "#fff",
        borderRadius: 12,
        boxShadow: "0 4px 20px rgba(0,0,0,0.08)"
    },

    messages: {
        flex: 1,
        padding: 20,
        overflowY: "auto"
    },

    bubble: {
        maxWidth: "70%",
        padding: 14,
        borderRadius: 16,
        whiteSpace: "pre-wrap"
    },

    typing: {
        fontSize: 12,
        color: "#999",
        marginTop: 6
    },

    inputBar: {
        display: "flex",
        gap: 10,
        padding: 14,
        borderTop: "1px solid #eee",
        background: "#fff"
    },

    input: {
        flex: 1,
        padding: 12,
        borderRadius: 10,
        border: "1px solid #ddd",
        outline: "none"
    },

    sendBtn: {
        padding: "0 20px",
        borderRadius: 10,
        border: "none",
        background: "#6c63ff",
        color: "#fff",
        cursor: "pointer"
    },

    downloadBtn: {
        marginTop: 4,
        padding: "6px 10px",
        borderRadius: 6,
        border: "none",
        cursor: "pointer"
    }
};
