import React, { useEffect, useRef, useState } from "react";
import { ChatFlow } from "./ChatFlow";
import { DOCUMENT_TYPES } from "./models/documentTypes";
import { downloadDocx } from "./fileUtils";
import { useAuth } from "../../auth/AuthContext";
import { useChatHistory } from "../../hooks/useChatHistory";
import ChatHistorySidebar from "./ChatHistorySidebar";

// Create ONE flow instance (acts like a controller)
const chatFlow = new ChatFlow(DOCUMENT_TYPES);

const ChatUI = ({ messageService }) => {
    const { user } = useAuth();
    const { 
        conversations, 
        messages: historyMessages, 
        loading: historyLoading, 
        saveMessage, 
        startNewConversation, 
        selectConversation,
        deleteConversation
    } = useChatHistory(user?.uid);

    const [mode, setMode] = useState("generation"); // generation | analysis | qa
    
    const [localMessages, setLocalMessages] = useState([
        {
            sender: "bot",
            text: "üëã Salut! Ce tip de document dore»ôti sƒÉ generezi?\n(cerere / contract / procura)"
        }
    ]);

    const [input, setInput] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [isHistoryOpen, setIsHistoryOpen] = useState(false);

    const messagesEndRef = useRef(null);
    const fileInputRef = useRef(null);

    // If historyMessages are loaded (meaning a conversation is selected), use them.
    // Otherwise, use localMessages (for new/unauthenticated sessions).
    const messages = (user && historyMessages.length > 0) ? historyMessages : localMessages;

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages, isLoading]);

    // When a conversation is selected, we might want to switch mode if we stored it.
    // Currently useChatHistory doesn't return the mode of the current conversation easily 
    // without looking it up in 'conversations' array.
    // Let's do that lookup.
    const handleSelectConversation = (id) => {
        selectConversation(id);
        const conv = conversations.find(c => c.id === id);
        if (conv && conv.mode) {
            setMode(conv.mode);
        }
        setIsHistoryOpen(false);
    };

    const handleNewConversation = () => {
        // When starting new, we keep the CURRENT mode selected by the user
        startNewConversation("Conversa»õie NouƒÉ", mode);
        
        let initialText = "";
        if (mode === "generation") initialText = "üëã Salut! Ce tip de document dore»ôti sƒÉ generezi?\n(cerere / contract / procura)";
        else if (mode === "analysis") initialText = "üëã Salut! √éncarcƒÉ un document PDF pentru analizƒÉ.";
        else if (mode === "qa") initialText = "üëã Salut! Cu ce problemƒÉ legalƒÉ te pot ajuta?";

        setLocalMessages([{
            sender: "bot",
            text: initialText
        }]);
        
        if (mode === "generation") chatFlow.reset();
        setIsHistoryOpen(false);
    };

    // Switch mode handler (resets current view if it's a new session)
    const handleModeChange = (newMode) => {
        setMode(newMode);
        // If we are in a "new" session (localMessages), reset it.
        // If we are in history, we probably should start a new conversation or just switch context?
        // Usually switching mode implies starting a new task.
        // Let's auto-start a new conversation context locally.
        
        // Reset local state
        let initialText = "";
        if (newMode === "generation") initialText = "üëã Salut! Ce tip de document dore»ôti sƒÉ generezi?\n(cerere / contract / procura)";
        else if (newMode === "analysis") initialText = "üëã Salut! √éncarcƒÉ un document PDF pentru analizƒÉ.";
        else if (newMode === "qa") initialText = "üëã Salut! Cu ce problemƒÉ legalƒÉ te pot ajuta?";

        setLocalMessages([{ sender: "bot", text: initialText }]);
        
        // If user is logged in, we don't force a new DB conversation yet until they send a message.
        // But we should clear the current selection so they aren't appending to an old wrong-mode chat.
        if (user) {
            selectConversation(null); 
        }
        
        if (newMode === "generation") chatFlow.reset();
    };

    const addMsg = (msg) => {
        setLocalMessages(prev => [...prev, msg]);
        if (user) {
            saveMessage(msg, mode);
        }
    };

    const handleFileUpload = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        addMsg({ sender: "user", text: `üìé Am √ÆncƒÉrcat: ${file.name}` });
        setIsLoading(true);

        // Mock analysis
        setTimeout(() => {
            addMsg({ sender: "bot", text: `‚úÖ Am analizat ${file.name}. Pare a fi un contract valid. (Mock Analysis)` });
            setIsLoading(false);
        }, 1500);
    };

    const sendMessage = async () => {
        if (!input.trim() || isLoading) return;

        const userInput = input.trim();
        setInput("");

        addMsg({ sender: "user", text: userInput });

        try {
            if (mode === "generation") {
                // --- GENERATION LOGIC ---
                if (!chatFlow.isDocumentSelected()) {
                    const question = chatFlow.selectDocument(userInput.toLowerCase());
                    addMsg({ sender: "bot", text: question });
                    return;
                }
                const result = chatFlow.handleAnswer(userInput);
                if (!result.done) {
                    addMsg({ sender: "bot", text: result.nextQuestion });
                    return;
                }
                setIsLoading(true);
                const blob = await messageService.sendMessage(result.payload);
                addMsg({ sender: "bot", text: "üìÑ Documentul este gata!", fileBlob: blob });
                addMsg({ sender: "bot", text: "Vrei sƒÉ generezi un alt document?" });
                chatFlow.reset();
                setIsLoading(false);

            } else if (mode === "qa") {
                // --- Q&A LOGIC ---
                setIsLoading(true);
                // Mock Q&A response
                setTimeout(() => {
                    addMsg({ sender: "bot", text: `ü§ñ RƒÉspuns legal la: "${userInput}"\n(Aceasta este o func»õionalitate Q&A simulatƒÉ)` });
                    setIsLoading(false);
                }, 1000);

            } else if (mode === "analysis") {
                // --- ANALYSIS LOGIC ---
                addMsg({ sender: "bot", text: "Te rog √ÆncarcƒÉ un fi»ôier PDF folosind butonul de ata»ôare pentru analizƒÉ." });
            }

        } catch (err) {
            console.error(err);
            addMsg({ sender: "bot", text: "‚ùå A apƒÉrut o eroare. Te rog √ÆncearcƒÉ din nou." });
            chatFlow.reset();
            setIsLoading(false);
        }
    };

    return (
        <div style={styles.wrapper}>
            <style>{`
                .history-btn {
                    background: transparent;
                    border: none;
                    cursor: pointer;
                    color: #6B7280;
                    font-size: 14px;
                    font-weight: 500;
                    display: flex;
                    align-items: center;
                    gap: 8px;
                    padding: 8px 12px;
                    border-radius: 8px;
                    transition: all 0.2s ease;
                }
                .history-btn:hover {
                    background-color: #F3F4F6;
                    color: #4F46E5;
                }
                .history-btn.active {
                    color: #4F46E5;
                    background-color: #EEF2FF;
                }
                .mode-tab {
                    padding: 8px 16px;
                    border-radius: 20px;
                    border: none;
                    cursor: pointer;
                    font-size: 13px;
                    font-weight: 600;
                    transition: all 0.2s;
                    background: #f3f4f6;
                    color: #6b7280;
                }
                .mode-tab.active {
                    background: #4F46E5;
                    color: white;
                }
            `}</style>

            <div style={styles.chatContainer}>
                {/* Header */}
                <div style={styles.header}>
                    {/* Mode Selector */}
                    <div style={{ display: 'flex', gap: 8 }}>
                        <button 
                            className={`mode-tab ${mode === 'generation' ? 'active' : ''}`}
                            onClick={() => handleModeChange('generation')}
                        >
                            Generare
                        </button>
                        <button 
                            className={`mode-tab ${mode === 'analysis' ? 'active' : ''}`}
                            onClick={() => handleModeChange('analysis')}
                        >
                            AnalizƒÉ
                        </button>
                        <button 
                            className={`mode-tab ${mode === 'qa' ? 'active' : ''}`}
                            onClick={() => handleModeChange('qa')}
                        >
                            Q&A
                        </button>
                    </div>

                    {user && (
                        <button 
                            onClick={() => setIsHistoryOpen(!isHistoryOpen)}
                            className={`history-btn ${isHistoryOpen ? 'active' : ''}`}
                        >
                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="history-icon" style={{width: 18, height: 18}}>
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                            </svg>
                            <span>Istoric</span>
                        </button>
                    )}
                </div>

                <div style={styles.messages}>
                    {messages.map((msg, i) => (
                        <div
                            key={i}
                            style={{
                                display: "flex",
                                justifyContent:
                                    msg.sender === "user" ? "flex-end" : "flex-start",
                                marginBottom: 10
                            }}
                        >
                            <div
                                style={{
                                    ...styles.bubble,
                                    background:
                                        msg.sender === "user" ? "#6c63ff" : "#f2f2f2",
                                    color: msg.sender === "user" ? "#fff" : "#000"
                                }}
                            >
                                {msg.text}

                                {(msg.fileBlob || msg.fileUrl) && (
                                    <div style={{ marginTop: 10 }}>
                                        <button
                                            style={styles.downloadBtn}
                                            onClick={() => {
                                                if (msg.fileBlob) {
                                                    downloadDocx(msg.fileBlob, "document.docx");
                                                } else if (msg.fileUrl) {
                                                    window.open(msg.fileUrl, "_blank");
                                                }
                                            }}
                                        >
                                            ‚¨áÔ∏è DescarcƒÉ documentul
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    ))}

                    {isLoading && (
                        <div style={styles.typing}>Bot scrie...</div>
                    )}

                    <div ref={messagesEndRef} />
                </div>

                {/* INPUT BAR */}
                <div style={styles.inputBar}>
                    {mode === 'analysis' && (
                        <>
                            <input 
                                type="file" 
                                ref={fileInputRef}
                                style={{ display: 'none' }}
                                accept=".pdf"
                                onChange={handleFileUpload}
                            />
                            <button 
                                onClick={() => fileInputRef.current?.click()}
                                style={styles.attachBtn}
                                title="√éncarcƒÉ PDF"
                            >
                                üìé
                            </button>
                        </>
                    )}
                    
                    <input
                        value={input}
                        onChange={e => setInput(e.target.value)}
                        onKeyDown={e => e.key === "Enter" && sendMessage()}
                        placeholder={mode === 'analysis' ? "√éncarcƒÉ un fi»ôier..." : "Scrie mesajul..."}
                        style={styles.input}
                        disabled={isLoading}
                    />
                    <button
                        onClick={sendMessage}
                        style={styles.sendBtn}
                        disabled={isLoading}
                    >
                        Trimite
                    </button>
                </div>
            </div>

            <ChatHistorySidebar 
                isOpen={isHistoryOpen} 
                onClose={() => setIsHistoryOpen(false)} 
                conversations={conversations}
                onSelectConversation={handleSelectConversation}
                onNewConversation={handleNewConversation}
                onDeleteConversation={deleteConversation}
                loading={historyLoading}
            />
        </div>
    );
};

export default ChatUI;

