    wrapper: {
        height: "100%",
        display: "flex",
        position: "relative",
        overflow: "hidden"
    },

    chatContainer: {
        flex: 1,
        height: "100%",
        display: "flex",
        flexDirection: "column",
        background: "#fff",
        position: "relative"
    },

    header: {
        padding: "12px 24px",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        background: "#fff", 
    },

    messages: {
        flex: 1,
        padding: 20,
        overflowY: "auto"
    },

    bubble: {
        maxWidth: "70%",
        padding: 14,
        borderRadius: 16,
        whiteSpace: "pre-wrap"
    },

    typing: {
        fontSize: 12,
        color: "#999",
        marginTop: 6
    },

    inputBar: {
        display: "flex",
        gap: 10,
        padding: 14,
        borderTop: "1px solid #eee",
        background: "#fff"
    },

    input: {
        flex: 1,
        padding: 12,
        borderRadius: 10,
        border: "1px solid #ddd",
        outline: "none"
    },

    sendBtn: {
        padding: "0 20px",
        borderRadius: 10,
        border: "none",
        background: "#6c63ff",
        color: "#fff",
        cursor: "pointer"
    },

    attachBtn: {
        padding: "0 14px",
        borderRadius: 10,
        border: "1px solid #ddd",
