import React, { useEffect, useRef, useState } from "react";
import { ChatFlow } from "./ChatFlow";
import { DOCUMENT_TYPES } from "./models/documentTypes";
import { downloadDocx } from "./fileUtils";

// Create ONE flow instance (acts like a controller)
const chatFlow = new ChatFlow(DOCUMENT_TYPES);

const ChatUI = ({ messageService }) => {
    const [messages, setMessages] = useState([
        {
            sender: "bot",
            text: "üëã Salut! Ce tip de document dore»ôti sƒÉ generezi?\n(cerere / contract / procura)"
        }
    ]);

    const [input, setInput] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    const messagesEndRef = useRef(null);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages, isLoading]);

    const sendMessage = async () => {
        if (!input.trim() || isLoading) return;

        const userInput = input.trim();
        setInput("");

        // show user message
        setMessages(prev => [...prev, { sender: "user", text: userInput }]);

        try {
            // STEP 1 ‚Äì select document
            if (!chatFlow.isDocumentSelected()) {
                const question = chatFlow.selectDocument(userInput.toLowerCase());

                setMessages(prev => [
                    ...prev,
                    { sender: "bot", text: question }
                ]);
                return;
            }

            // STEP 2 ‚Äì collect answers
            const result = chatFlow.handleAnswer(userInput);

            if (!result.done) {
                setMessages(prev => [
                    ...prev,
                    { sender: "bot", text: result.nextQuestion }
                ]);
                return;
            }

            // STEP 3 ‚Äì generate document
            setIsLoading(true);
            setMessages(prev => [
                ...prev,
                { sender: "bot", text: "‚è≥ Generez documentul..." }
            ]);

            const blob = await messageService.sendMessage(result.payload);

            setMessages(prev => [
                ...prev,
                {
                    sender: "bot",
                    text: "üìÑ Documentul este gata!",
                    fileBlob: blob
                },
                {
                    sender: "bot",
                    text: "Vrei sƒÉ generezi un alt document?"
                }
            ]);

            chatFlow.reset();
        } catch (err) {
            console.error(err);
            setMessages(prev => [
                ...prev,
                { sender: "bot", text: "‚ùå A apƒÉrut o eroare. Te rog √ÆncearcƒÉ din nou." }
            ]);
            chatFlow.reset();
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div style={styles.wrapper}>
            <div style={styles.chatContainer}>
                <div style={styles.messages}>
                    {messages.map((msg, i) => (
                        <div
                            key={i}
                            style={{
                                display: "flex",
                                justifyContent:
                                    msg.sender === "user" ? "flex-end" : "flex-start",
                                marginBottom: 10
                            }}
                        >
                            <div
                                style={{
                                    ...styles.bubble,
                                    background:
                                        msg.sender === "user" ? "#6c63ff" : "#f2f2f2",
                                    color: msg.sender === "user" ? "#fff" : "#000"
                                }}
                            >
                                {msg.text}

                                {msg.fileBlob && (
                                    <div style={{ marginTop: 10 }}>
                                        <button
                                            style={styles.downloadBtn}
                                            onClick={() =>
                                                downloadDocx(msg.fileBlob, "document.docx")
                                            }
                                        >
                                            ‚¨áÔ∏è DescarcƒÉ documentul
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    ))}

                    {isLoading && (
                        <div style={styles.typing}>Bot scrie...</div>
                    )}

                    <div ref={messagesEndRef} />
                </div>

                {/* INPUT BAR */}
                <div style={styles.inputBar}>
                    <input
                        value={input}
                        onChange={e => setInput(e.target.value)}
                        onKeyDown={e => e.key === "Enter" && sendMessage()}
                        placeholder="Scrie rƒÉspunsul..."
                        style={styles.input}
                        disabled={isLoading}
                    />
                    <button
                        onClick={sendMessage}
                        style={styles.sendBtn}
                        disabled={isLoading}
                    >
                        Trimite
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ChatUI;

const styles = {
    wrapper: {
        height: "100%",
        display: "flex"
    },

    chatContainer: {
        width: "100%",
        height: "100%",
        display: "flex",
        flexDirection: "column",
        background: "#fff"
    },

    messages: {
        flex: 1,
        padding: 20,
        overflowY: "auto"
    },

    bubble: {
        maxWidth: "70%",
        padding: 14,
        borderRadius: 16,
        whiteSpace: "pre-wrap"
    },

    typing: {
        fontSize: 12,
        color: "#999",
        marginTop: 6
    },

    inputBar: {
        display: "flex",
        gap: 10,
        padding: 14,
        borderTop: "1px solid #eee",
        background: "#fff"
    },

    input: {
        flex: 1,
        padding: 12,
        borderRadius: 10,
        border: "1px solid #ddd",
        outline: "none"
    },

    sendBtn: {
        padding: "0 20px",
        borderRadius: 10,
        border: "none",
        background: "#6c63ff",
        color: "#fff",
        cursor: "pointer"
    },

    downloadBtn: {
        marginTop: 4,
        padding: "6px 10px",
        borderRadius: 6,
        border: "none",
        cursor: "pointer"
    }
};
