import React, { useState, useEffect } from 'react';
// Importați funcțiile necesare din Firestore
import { collection, getDocs, doc, getDoc } from 'firebase/firestore';
// Asigurați-vă că 'db' este importat corect din fișierul dumneavoastră de configurare
import { db } from './firebase';

// Iconițele necesare
import { FileText, Gavel, BookOpen, Briefcase, X } from "lucide-react";

// 1. MAPAREA ICONIȚELOR
// Nu putem stoca direct componente React (cum ar fi <Briefcase />) în Firestore.
// Stocăm un *nume* de iconiță în Firestore (e.g., "Briefcase") și îl mapăm aici.
const IconMap = {
    Contracte: Briefcase,
    Cereri: FileText,
    Penal: Gavel,
    Civil: BookOpen,
};

// Categoriile rămân aceleași pentru butoanele de filtrare
const categorii = ["Toate", "Contracte", "Cereri", "Penal", "Civil"];

const DocumentePrestabilite = () => {
    // Stări pentru datele preluate
    const [documents, setDocuments] = useState([]); // Aici stocăm lista de modele
    const [loading, setLoading] = useState(true); // Stare de încărcare inițială
    const [error, setError] = useState(null);

    // Stări pentru filtrare
    const [categorieSelectata, setCategorieSelectata] = useState("Toate");

    // Stări pentru Modal/Conținut (Noul Feature)
    const [selectedDocument, setSelectedDocument] = useState(null); // Documentul complet selectat
    const [isModalOpen, setIsModalOpen] = useState(false); // Stare Modal

    // 2. FUNCTIE PENTRU PRELUAREA LISTEI DE MODELE (Metadata)
    useEffect(() => {
        const fetchDocuments = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "documentModels"));
                const documentsData = querySnapshot.docs.map(doc => ({
                    id: doc.id,
                    ...doc.data()
                }));
                setDocuments(documentsData);
                setLoading(false);
            } catch (err) {
                console.error("Eroare la preluarea documentelor:", err);
                setError("Nu am putut încărca modelele de documente din Firebase.");
                setLoading(false);
            }
        };

        fetchDocuments();
    }, []); // Se rulează doar la montarea componentei

    // 3. FUNCTIE PENTRU PRELUAREA CONTINUTULUI LA CLICK
    const handleOpenDocument = async (documentId) => {
        // Punem documentul selectat pe null și deschidem modalul (pentru a afișa un loading/spinner)
        setSelectedDocument(null);
        setIsModalOpen(true);

        try {
            // Referință către documentul specific în colecția "documentModels"
            const docRef = doc(db, "documentModels", documentId);

            // Preluarea documentului complet (care include câmpul 'continut')
            const docSnap = await getDoc(docRef);

            if (docSnap.exists()) {
                setSelectedDocument({
                    id: docSnap.id,
                    ...docSnap.data()
                });
            } else {
                alert("Conținutul documentului nu a fost găsit.");
                setIsModalOpen(false);
            }

        } catch (error) {
            console.error("Eroare la preluarea conținutului:", error);
            alert("A apărut o eroare la încărcarea conținutului.");
            setIsModalOpen(false);
        }
    };

    // 4. LOGICA DE FILTRARE (folosind datele dinamice)
    const filtrate = categorieSelectata === "Toate"
        ? documents
        : documents.filter(d => d.categorie === categorieSelectata);

    // 5. AFIȘAREA STĂRII DE ÎNCĂRCARE SAU EROARE
    if (loading) {
        return <p style={{ padding: "20px", fontSize: "18px" }}>Se încarcă modelele de documente...</p>;
    }

    if (error) {
        return <p style={{ padding: "20px", fontSize: "18px", color: "red" }}>Eroare: {error}</p>;
    }


    return (
        <div style={{ padding: "20px" }}>
            <h1 style={{ fontSize: "28px", marginBottom: "20px", fontWeight: "bold" }}>
                Documente Prestabilite
            </h1>

            {/* FILTRE */}
            <div style={{ marginBottom: "20px", display: "flex", gap: "10px", flexWrap: "wrap" }}>
                {categorii.map(cat => (
                    <button
                        key={cat}
                        onClick={() => setCategorieSelectata(cat)}
                        style={{
                            padding: "10px 15px",
                            borderRadius: "8px",
                            border: "1px solid #ccc",
                            background: categorieSelectata === cat ? "#4e73df" : "white",
                            color: categorieSelectata === cat ? "white" : "black",
                            cursor: "pointer",
                        }}
                    >
                        {cat}
                    </button>
                ))}
            </div>

            {/* GRID DOCUMENTE */}
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
                    gap: "20px",
                }}
            >
                {filtrate.map(doc => {
                    // Preluăm componenta Iconiță din map, folosind categoria documentului
                    const IconComponent = IconMap[doc.categorie];

                    return (
                        <div
                            key={doc.id} // Folosim ID-ul din Firestore
                            style={{
                                padding: "20px",
                                borderRadius: "12px",
                                boxShadow: "0 0 12px rgba(0,0,0,0.1)",
                                background: "white",
                                display: "flex",
                                flexDirection: "column",
                                justifyContent: "space-between"
                            }}
                        >
                            <div>
                                {/* Afișăm iconița */}
                                <div style={{ marginBottom: "10px" }}>
                                    {IconComponent ? <IconComponent size={32} /> : <FileText size={32} />}
                                </div>
                                <h3 style={{ marginBottom: "10px", fontSize: "20px" }}>{doc.titlu}</h3>
                                <p style={{ fontSize: "14px", color: "#555" }}>{doc.descriere}</p>
                            </div>

                            <button
                                style={{
                                    marginTop: "15px",
                                    padding: "10px",
                                    borderRadius: "8px",
                                    background: "#4e73df",
                                    color: "white",
                                    border: "none",
                                    cursor: "pointer",
                                    fontWeight: "bold"
                                }}
                                // 6. APELĂM FUNCȚIA DE PRELUARE A CONȚINUTULUI
                                onClick={() => handleOpenDocument(doc.id)}
                            >
                                Deschide Documentul
                            </button>
                        </div>
                    );
                })}
            </div>

            {/* 7. MODALUL PENTRU AFIȘAREA CONȚINUTULUI */}
            {isModalOpen && (
                <div style={modalOverlayStyle}>
                    <div style={modalContentStyle}>
                        {selectedDocument ? (
                            <>
                                <h2 style={{ marginBottom: '15px' }}>{selectedDocument.titlu}</h2>
                                <hr style={{ marginBottom: '20px' }}/>
                                {/* ATENȚIE: Folosim 'dangerouslySetInnerHTML' pentru a afișa
                                    conținutul text/HTML din Firestore (câmpul 'continut').
                                    Asigurați-vă că datele din Firestore sunt sigure pentru a preveni atacurile XSS.
                                */}
                                <div
                                    style={{ maxHeight: '70vh', overflowY: 'auto', paddingRight: '15px' }}
                                    dangerouslySetInnerHTML={{ __html: selectedDocument.continut }}
                                />
                                <button onClick={() => setIsModalOpen(false)} style={closeButtonStyle}>
                                    Închide
                                </button>
                            </>
                        ) : (
                            // Stare de încărcare în interiorul Modalului
                            <p>Se încarcă conținutul documentului...</p>
                        )}
                        <button
                            onClick={() => setIsModalOpen(false)}
                            style={closeIconStyle}
                        >
                            <X size={24} />
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default DocumentePrestabilite;

// Obiecte de stil pentru Modal (pentru simplitate)
const modalOverlayStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,
};

const modalContentStyle = {
    backgroundColor: 'white',
    padding: '30px',
    borderRadius: '12px',
    width: '90%',
    maxWidth: '800px',
    boxShadow: '0 5px 15px rgba(0,0,0,0.3)',
    position: 'relative',
};

const closeButtonStyle = {
    marginTop: '20px',
    padding: '10px 20px',
    borderRadius: '8px',
    background: '#ccc',
    color: 'black',
    border: 'none',
    cursor: 'pointer',
    fontWeight: 'bold',
};

const closeIconStyle = {
    position: 'absolute',
    top: '15px',
    right: '15px',
    background: 'none',
    border: 'none',
    cursor: 'pointer',
};
